import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { UserPlus, Eye, EyeOff } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';

const Register = () => {
  const navigate = useNavigate();
  const { register, loading, error, clearError, isAuthenticated } = useAuth();

  const [formData, setFormData] = useState({
    username: '',
    email: '',
    name: '',
    password: '',
    confirmPassword: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [errors, setErrors] = useState({});

  // 로그인 상태면 홈으로 리다이렉트
  useEffect(() => {
    if (isAuthenticated) {
      navigate('/', { replace: true });
    }
  }, [isAuthenticated, navigate]);

  // 에러 상태 클리어
  useEffect(() => {
    return () => {
      clearError();
    };
  }, [clearError]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // 에러 메시지 제거
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    // 사용자명 검증
    if (!formData.username.trim()) {
      newErrors.username = '사용자명을 입력해주세요.';
    } else if (formData.username.length < 3) {
      newErrors.username = '사용자명은 최소 3자 이상이어야 합니다.';
    } else if (formData.username.length > 20) {
      newErrors.username = '사용자명은 20자를 초과할 수 없습니다.';
    } else if (!/^[a-zA-Z0-9_]+$/.test(formData.username)) {
      newErrors.username = '사용자명은 영문, 숫자, 언더스코어만 사용 가능합니다.';
    }

    // 이메일 검증
    if (!formData.email.trim()) {
      newErrors.email = '이메일을 입력해주세요.';
    } else if (!/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/.test(formData.email)) {
      newErrors.email = '올바른 이메일 형식을 입력해주세요.';
    }

    // 이름 검증
    if (!formData.name.trim()) {
      newErrors.name = '이름을 입력해주세요.';
    } else if (formData.name.length > 50) {
      newErrors.name = '이름은 50자를 초과할 수 없습니다.';
    }

    // 비밀번호 검증
    if (!formData.password) {
      newErrors.password = '비밀번호를 입력해주세요.';
    } else if (formData.password.length < 6) {
      newErrors.password = '비밀번호는 최소 6자 이상이어야 합니다.';
    }

    // 비밀번호 확인 검증
    if (!formData.confirmPassword) {
      newErrors.confirmPassword = '비밀번호 확인을 입력해주세요.';
    } else if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = '비밀번호가 일치하지 않습니다.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    const { confirmPassword, ...registerData } = formData;
    const result = await register(registerData);
    
    if (result.success) {
      navigate('/', { replace: true });
    }
  };

  return (
    <div className="min-h-screen flex flex-col justify-center py-12 sm:px-6 lg:px-8">
      <div className="sm:mx-auto sm:w-full sm:max-w-md">
        <div className="text-center">
          <h2 className="text-4xl font-bold text-white text-glow mb-2">✨ 회원가입</h2>
          <p className="text-white/70">
            이미 계정이 있으신가요?{' '}
            <Link
              to="/login"
              className="font-medium text-white hover:text-white/90 underline decoration-white/50 hover:decoration-white transition-all duration-200"
            >
              로그인하기
            </Link>
          </p>
        </div>
      </div>

      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
        <div className="glass rounded-2xl py-8 px-6 sm:px-10 border border-white/20 hover-glow">
          {/* 에러 메시지 */}
          {error && (
            <div className="mb-6 glass-light border border-red-300/50 rounded-xl p-4">
              <p className="text-sm text-red-200">{error}</p>
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-5">
            {/* 사용자명 */}
            <div>
              <label htmlFor="username" className="block text-sm font-medium text-white/90 mb-2">
                사용자명
              </label>
              <div className="mt-1">
                <input
                  id="username"
                  name="username"
                  type="text"
                  value={formData.username}
                  onChange={handleInputChange}
                  className={`glass-input appearance-none block w-full px-4 py-3 rounded-xl placeholder-white/50 text-white focus:ring-2 focus:ring-white/20 sm:text-sm transition-all duration-200 ${
                    errors.username ? 'border-red-300/50' : ''
                  }`}
                  placeholder="영문, 숫자, 언더스코어만 사용 가능"
                />
                {errors.username && (
                  <p className="mt-2 text-sm text-red-200">{errors.username}</p>
                )}
              </div>
            </div>

            {/* 이메일 */}
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-white/90 mb-2">
                이메일
              </label>
              <div className="mt-1">
                <input
                  id="email"
                  name="email"
                  type="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className={`glass-input appearance-none block w-full px-4 py-3 rounded-xl placeholder-white/50 text-white focus:ring-2 focus:ring-white/20 sm:text-sm transition-all duration-200 ${
                    errors.email ? 'border-red-300/50' : ''
                  }`}
                  placeholder="이메일을 입력하세요"
                />
                {errors.email && (
                  <p className="mt-2 text-sm text-red-200">{errors.email}</p>
                )}
              </div>
            </div>

            {/* 이름 */}
            <div>
              <label htmlFor="name" className="block text-sm font-medium text-white/90 mb-2">
                이름
              </label>
              <div className="mt-1">
                <input
                  id="name"
                  name="name"
                  type="text"
                  value={formData.name}
                  onChange={handleInputChange}
                  className={`glass-input appearance-none block w-full px-4 py-3 rounded-xl placeholder-white/50 text-white focus:ring-2 focus:ring-white/20 sm:text-sm transition-all duration-200 ${
                    errors.name ? 'border-red-300/50' : ''
                  }`}
                  placeholder="실명을 입력하세요"
                />
                {errors.name && (
                  <p className="mt-2 text-sm text-red-200">{errors.name}</p>
                )}
              </div>
            </div>

            {/* 비밀번호 */}
            <div>
              <label htmlFor="password" className="block text-sm font-medium text-white/90 mb-2">
                비밀번호
              </label>
              <div className="mt-1 relative">
                <input
                  id="password"
                  name="password"
                  type={showPassword ? 'text' : 'password'}
                  value={formData.password}
                  onChange={handleInputChange}
                  className={`glass-input appearance-none block w-full px-4 py-3 rounded-xl placeholder-white/50 text-white focus:ring-2 focus:ring-white/20 sm:text-sm pr-12 transition-all duration-200 ${
                    errors.password ? 'border-red-300/50' : ''
                  }`}
                  placeholder="최소 6자 이상"
                />
                <button
                  type="button"
                  className="absolute inset-y-0 right-0 pr-4 flex items-center text-white/60 hover:text-white transition-colors duration-200"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? (
                    <EyeOff className="h-5 w-5" />
                  ) : (
                    <Eye className="h-5 w-5" />
                  )}
                </button>
                {errors.password && (
                  <p className="mt-2 text-sm text-red-200">{errors.password}</p>
                )}
              </div>
            </div>

            {/* 비밀번호 확인 */}
            <div>
              <label htmlFor="confirmPassword" className="block text-sm font-medium text-white/90 mb-2">
                비밀번호 확인
              </label>
              <div className="mt-1 relative">
                <input
                  id="confirmPassword"
                  name="confirmPassword"
                  type={showConfirmPassword ? 'text' : 'password'}
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  className={`glass-input appearance-none block w-full px-4 py-3 rounded-xl placeholder-white/50 text-white focus:ring-2 focus:ring-white/20 sm:text-sm pr-12 transition-all duration-200 ${
                    errors.confirmPassword ? 'border-red-300/50' : ''
                  }`}
                  placeholder="비밀번호를 다시 입력하세요"
                />
                <button
                  type="button"
                  className="absolute inset-y-0 right-0 pr-4 flex items-center text-white/60 hover:text-white transition-colors duration-200"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? (
                    <EyeOff className="h-5 w-5" />
                  ) : (
                    <Eye className="h-5 w-5" />
                  )}
                </button>
                {errors.confirmPassword && (
                  <p className="mt-2 text-sm text-red-200">{errors.confirmPassword}</p>
                )}
              </div>
            </div>

            {/* 회원가입 버튼 */}
            <div className="pt-2">
              <button
                type="submit"
                disabled={loading}
                className="w-full glass-button text-white py-3 px-4 rounded-xl text-sm font-medium flex items-center justify-center disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? (
                  <>
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                    회원가입 중...
                  </>
                ) : (
                  <>
                    <UserPlus className="w-5 h-5 mr-2" />
                    회원가입
                  </>
                )}
              </button>
            </div>
          </form>

          {/* 홈으로 돌아가기 */}
          <div className="mt-6 text-center">
            <Link
              to="/"
              className="text-sm text-white/70 hover:text-white underline decoration-white/30 hover:decoration-white transition-all duration-200"
            >
              홈으로 돌아가기
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
