import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Search, Users, Eye, Calendar, Heart, BookOpen } from 'lucide-react';
import { scriptAPI, emotionAPI } from '../services/api';

const Home = () => {
  const [popularScripts, setPopularScripts] = useState([]);
  const [latestScripts, setLatestScripts] = useState([]);
  const [emotions, setEmotions] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [popularRes, latestRes, emotionsRes] = await Promise.all([
          scriptAPI.getPopular(),
          scriptAPI.getLatest(),
          emotionAPI.getAll()
        ]);
        
        setPopularScripts(popularRes.data);
        setLatestScripts(latestRes.data);
        setEmotions(emotionsRes.data);
      } catch (error) {
        console.error('Îç∞Ïù¥ÌÑ∞ Î°úÎî© Ïã§Ìå®:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/scripts?search=${encodeURIComponent(searchTerm)}`);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="spinner mx-auto"></div>
          <p className="mt-4 text-secondary">Î°úÎî© Ï§ë...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      {/* ÌûàÏñ¥Î°ú ÏÑπÏÖò */}
      <section className="hero py-16 lg:py-24">
        <div className="container">
          <div className="text-center max-w-4xl mx-auto">
            <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold mb-6">
              Ïó∞Í∏∞ ÎåÄÎ≥∏ ÎùºÏù¥Î∏åÎü¨Î¶¨
            </h1>
            <p className="text-xl md:text-2xl mb-8 opacity-90">
              Îã§ÏñëÌïú Í∞êÏ†ïÍ≥º ÏÉÅÌô©Ïùò Ïó∞Í∏∞ ÎåÄÎ≥∏ÏùÑ Ï∞æÏïÑÎ≥¥ÏÑ∏Ïöî
            </p>
            
            {/* Í≤ÄÏÉâ Ìèº */}
            <form onSubmit={handleSearch} className="search-container mb-8">
              <div className="flex bg-white rounded-lg overflow-hidden shadow-lg">
                <input
                  type="text"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  placeholder="Ï†úÎ™©, ÏÉÅÌô©, Í∞êÏ†ïÏúºÎ°ú Í≤ÄÏÉâ..."
                  className="search-input"
                />
                <button
                  type="submit"
                  className="search-button"
                >
                  <Search className="w-5 h-5" />
                </button>
              </div>
            </form>

            {/* CTA Î≤ÑÌäºÎì§ */}
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link to="/scripts" className="btn btn-secondary">
                <BookOpen className="w-5 h-5 mr-2" />
                ÎåÄÎ≥∏ ÎëòÎü¨Î≥¥Í∏∞
              </Link>
              <Link to="/add-script" className="btn bg-white text-emerald-600 hover:bg-gray-100">
                <Heart className="w-5 h-5 mr-2" />
                ÎåÄÎ≥∏ Îì±Î°ùÌïòÍ∏∞
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Î©îÏù∏ ÏΩòÌÖêÏ∏† */}
      <div className="container py-16">
        <div className="space-y-16">
          
          {/* ÌÜµÍ≥Ñ ÏÑπÏÖò */}
          <section>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              <div className="stat-card">
                <div className="stat-icon">
                  <BookOpen className="w-6 h-6" />
                </div>
                <div className="stat-number">1,000+</div>
                <div className="stat-label">Îã§ÏñëÌïú ÎåÄÎ≥∏Îì§</div>
              </div>
              
              <div className="stat-card">
                <div className="stat-icon">
                  <Users className="w-6 h-6" />
                </div>
                <div className="stat-number">500+</div>
                <div className="stat-label">ÌôúÎ∞úÌïú ÏÇ¨Ïö©Ïûê</div>
              </div>
              
              <div className="stat-card">
                <div className="stat-icon">
                  <Heart className="w-6 h-6" />
                </div>
                <div className="stat-number">50+</div>
                <div className="stat-label">Í∞êÏ†ï Ïπ¥ÌÖåÍ≥†Î¶¨</div>
              </div>
            </div>
          </section>

          {/* Ïù∏Í∏∞ ÎåÄÎ≥∏ & ÏµúÏã† ÎåÄÎ≥∏ ÏÑπÏÖò */}
          <section>
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              
              {/* Ïù∏Í∏∞ ÎåÄÎ≥∏ */}
              <div className="space-y-6">
                <div className="flex-between">
                  <h2 className="section-title">Ïù∏Í∏∞ ÎåÄÎ≥∏</h2>
                  <Link
                    to="/scripts?sort=popular"
                    className="text-primary hover:text-emerald-700 text-sm font-medium"
                  >
                    Îçî Î≥¥Í∏∞ ‚Üí
                  </Link>
                </div>
                
                <div className="card">
                  <div className="space-y-4">
                    {popularScripts.slice(0, 4).map((script) => (
                      <ScriptListItem key={script._id} script={script} />
                    ))}
                  </div>
                </div>
              </div>

              {/* ÏµúÏã† ÎåÄÎ≥∏ */}
              <div className="space-y-6">
                <div className="flex-between">
                  <h2 className="section-title">ÏµúÏã† ÎåÄÎ≥∏</h2>
                  <Link
                    to="/scripts?sort=latest"
                    className="text-primary hover:text-emerald-700 text-sm font-medium"
                  >
                    Îçî Î≥¥Í∏∞ ‚Üí
                  </Link>
                </div>
                
                <div className="card">
                  <div className="space-y-4">
                    {latestScripts.slice(0, 4).map((script) => (
                      <ScriptListItem key={script._id} script={script} />
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </section>

          {/* Í∞êÏ†ï Ïπ¥ÌÖåÍ≥†Î¶¨ ÏÑπÏÖò */}
          <section>
            <div className="text-center mb-12">
              <h2 className="section-title">Í∞êÏ†ïÎ≥Ñ ÎåÄÎ≥∏ Ï∞æÍ∏∞</h2>
              <p className="text-secondary max-w-2xl mx-auto">
                ÏõêÌïòÎäî Í∞êÏ†ïÏù¥ÎÇò ÏÉÅÌô©Ïóê ÎßûÎäî ÎåÄÎ≥∏ÏùÑ ÏâΩÍ≤å Ï∞æÏïÑÎ≥¥ÏÑ∏Ïöî
              </p>
            </div>
            
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              {emotions.slice(0, 8).map((emotion) => (
                <Link
                  key={emotion._id}
                  to={`/scripts?emotion=${emotion._id}`}
                  className="card-hover text-center group"
                >
                  <div className="w-16 h-16 bg-gradient-to-br from-emerald-100 to-emerald-200 rounded-full flex items-center justify-center mx-auto mb-4 group-hover:scale-110 transition-transform">
                    <span className="text-2xl">{emotion.icon || 'üé≠'}</span>
                  </div>
                  <h3 className="font-semibold text-gray-900 mb-2">{emotion.name}</h3>
                  <p className="text-sm text-secondary">{emotion.description}</p>
                </Link>
              ))}
            </div>
          </section>

          {/* CTA ÏÑπÏÖò */}
          <section className="bg-gradient-to-r from-emerald-50 to-emerald-100 rounded-2xl p-8 lg:p-12 text-center">
            <h2 className="text-2xl lg:text-3xl font-bold text-gray-900 mb-4">
              ÎÇòÎßåÏùò ÎåÄÎ≥∏ÏùÑ Í≥µÏú†Ìï¥Î≥¥ÏÑ∏Ïöî
            </h2>
            <p className="text-lg text-secondary mb-8 max-w-2xl mx-auto">
              ÎãπÏã†Ïùò Ï∞ΩÏûë ÎåÄÎ≥∏ÏùÑ Îã§Î•∏ Î∞∞Ïö∞Îì§Í≥º Í≥µÏú†ÌïòÍ≥†, 
              ÌîºÎìúÎ∞±ÏùÑ Î∞õÏïÑÎ≥¥ÏÑ∏Ïöî
            </p>
            <Link to="/add-script" className="btn btn-primary">
              <Heart className="w-5 h-5 mr-2" />
              ÎåÄÎ≥∏ Îì±Î°ùÌïòÍ∏∞
            </Link>
          </section>

        </div>
      </div>
    </div>
  );
};

// ScriptListItem Ïª¥Ìè¨ÎÑåÌä∏
const ScriptListItem = ({ script }) => {
  return (
    <Link
      to={`/scripts/${script._id}`}
      className="script-item group"
    >
      <div className="flex items-start justify-between">
        <div className="flex-1">
          <h3 className="font-semibold text-gray-900 group-hover:text-emerald-600 mb-2">
            {script.title}
          </h3>
          <p className="text-sm text-secondary mb-3 line-clamp-2">
            {script.situation}
          </p>
          <div className="flex items-center gap-4 text-xs text-gray-500">
            <span className="flex items-center">
              <Eye className="w-3 h-3 mr-1" />
              {script.views || 0}
            </span>
            <span className="flex items-center">
              <Calendar className="w-3 h-3 mr-1" />
              {new Date(script.createdAt).toLocaleDateString()}
            </span>
          </div>
        </div>
        {script.emotion && (
          <span className="emotion-tag ml-4">
            {script.emotion.name}
          </span>
        )}
      </div>
    </Link>
  );
};

export default Home;
