import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { LogIn, Eye, EyeOff } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';

const Login = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { login, loading, error, clearError, isAuthenticated } = useAuth();

  const [formData, setFormData] = useState({
    loginId: '',
    password: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});

  // 로그인 상태면 홈으로 리다이렉트
  useEffect(() => {
    if (isAuthenticated) {
      const from = location.state?.from?.pathname || '/';
      navigate(from, { replace: true });
    }
  }, [isAuthenticated, navigate, location]);

  // 에러 상태 클리어
  useEffect(() => {
    return () => {
      clearError();
    };
  }, [clearError]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // 에러 메시지 제거
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.loginId.trim()) {
      newErrors.loginId = '사용자명 또는 이메일을 입력해주세요.';
    }

    if (!formData.password) {
      newErrors.password = '비밀번호를 입력해주세요.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    const result = await login(formData);
    
    if (result.success) {
      const from = location.state?.from?.pathname || '/';
      navigate(from, { replace: true });
    }
  };

  return (
    <div className="min-h-screen flex flex-col justify-center py-12 sm:px-6 lg:px-8">
      <div className="sm:mx-auto sm:w-full sm:max-w-md">
        <div className="text-center">
          <h2 className="text-4xl font-bold text-white text-glow mb-2">✨ 로그인</h2>
          <p className="text-white/70">
            계정이 없으신가요?{' '}
            <Link
              to="/register"
              className="font-medium text-white hover:text-white/90 underline decoration-white/50 hover:decoration-white transition-all duration-200"
            >
              회원가입하기
            </Link>
          </p>
        </div>
      </div>

      <div className="mt-8 sm:mx-auto sm:w-full sm:max-w-md">
        <div className="glass rounded-2xl py-8 px-6 sm:px-10 border border-white/20 hover-glow">
          {/* 에러 메시지 */}
          {error && (
            <div className="mb-6 glass-light border border-red-300/50 rounded-xl p-4">
              <p className="text-sm text-red-200">{error}</p>
            </div>
          )}

          {/* 로그인 안내 메시지 */}
          {location.state?.message && (
            <div className="mb-6 glass-light border border-blue-300/50 rounded-xl p-4">
              <p className="text-sm text-blue-200">{location.state.message}</p>
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-6">
            {/* 사용자명/이메일 */}
            <div>
              <label htmlFor="loginId" className="block text-sm font-medium text-white/90 mb-2">
                사용자명 또는 이메일
              </label>
              <div className="mt-1">
                <input
                  id="loginId"
                  name="loginId"
                  type="text"
                  value={formData.loginId}
                  onChange={handleInputChange}
                  className={`glass-input appearance-none block w-full px-4 py-3 rounded-xl placeholder-white/50 text-white focus:ring-2 focus:ring-white/20 sm:text-sm transition-all duration-200 ${
                    errors.loginId ? 'border-red-300/50' : ''
                  }`}
                  placeholder="사용자명 또는 이메일을 입력하세요"
                />
                {errors.loginId && (
                  <p className="mt-2 text-sm text-red-200">{errors.loginId}</p>
                )}
              </div>
            </div>

            {/* 비밀번호 */}
            <div>
              <label htmlFor="password" className="block text-sm font-medium text-white/90 mb-2">
                비밀번호
              </label>
              <div className="mt-1 relative">
                <input
                  id="password"
                  name="password"
                  type={showPassword ? 'text' : 'password'}
                  value={formData.password}
                  onChange={handleInputChange}
                  className={`glass-input appearance-none block w-full px-4 py-3 rounded-xl placeholder-white/50 text-white focus:ring-2 focus:ring-white/20 sm:text-sm pr-12 transition-all duration-200 ${
                    errors.password ? 'border-red-300/50' : ''
                  }`}
                  placeholder="비밀번호를 입력하세요"
                />
                <button
                  type="button"
                  className="absolute inset-y-0 right-0 pr-4 flex items-center text-white/60 hover:text-white transition-colors duration-200"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? (
                    <EyeOff className="h-5 w-5" />
                  ) : (
                    <Eye className="h-5 w-5" />
                  )}
                </button>
                {errors.password && (
                  <p className="mt-2 text-sm text-red-200">{errors.password}</p>
                )}
              </div>
            </div>

            {/* 로그인 버튼 */}
            <div>
              <button
                type="submit"
                disabled={loading}
                className="w-full glass-button text-white py-3 px-4 rounded-xl text-sm font-medium flex items-center justify-center disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? (
                  <>
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-3"></div>
                    로그인 중...
                  </>
                ) : (
                  <>
                    <LogIn className="w-5 h-5 mr-2" />
                    로그인
                  </>
                )}
              </button>
            </div>
          </form>

          {/* 홈으로 돌아가기 */}
          <div className="mt-6 text-center">
            <Link
              to="/"
              className="text-sm text-white/70 hover:text-white underline decoration-white/30 hover:decoration-white transition-all duration-200"
            >
              홈으로 돌아가기
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
