import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Search, Users, Eye, Calendar, Heart } from 'lucide-react';
import { scriptAPI, emotionAPI } from '../services/api';

const Home = () => {
  const [popularScripts, setPopularScripts] = useState([]);
  const [latestScripts, setLatestScripts] = useState([]);
  const [emotions, setEmotions] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [popularRes, latestRes, emotionsRes] = await Promise.all([
          scriptAPI.getPopular(),
          scriptAPI.getLatest(),
          emotionAPI.getAll()
        ]);
        
        setPopularScripts(popularRes.data);
        setLatestScripts(latestRes.data);
        setEmotions(emotionsRes.data);
      } catch (error) {
        console.error('데이터 로딩 실패:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/scripts?search=${encodeURIComponent(searchTerm)}`);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('ko-KR');
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">로딩 중...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* 헤로 섹션 */}
      <section className="bg-gradient-to-r from-primary-600 to-primary-700 text-white py-16">
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-4xl md:text-6xl font-bold mb-6">
            연기 대본 라이브러리
          </h1>
          <p className="text-xl mb-8">
            다양한 감정과 상황의 연기 대본을 찾아보세요
          </p>
          
          {/* 검색 폼 */}
          <form onSubmit={handleSearch} className="max-w-2xl mx-auto">
            <div className="flex bg-white rounded-lg overflow-hidden shadow-lg">
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="제목, 상황, 감정으로 검색..."
                className="flex-1 px-6 py-4 text-gray-800 focus:outline-none"
              />
              <button
                type="submit"
                className="bg-primary-500 hover:bg-primary-600 px-8 py-4 transition duration-200"
              >
                <Search className="w-5 h-5" />
              </button>
            </div>
          </form>
        </div>
      </section>

      <div className="container mx-auto px-4 py-12">
        {/* 인기 대본 섹션 */}
        <section className="mb-12">
          <div className="flex items-center justify-between mb-8">
            <h2 className="text-3xl font-bold text-gray-800 flex items-center">
              <Heart className="w-8 h-8 text-red-500 mr-3" />
              인기 대본
            </h2>
            <Link
              to="/scripts?sort=popular"
              className="text-primary-600 hover:text-primary-700 font-medium"
            >
              더 보기 →
            </Link>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {popularScripts.map((script) => (
              <ScriptCard key={script._id} script={script} />
            ))}
          </div>
        </section>

        {/* 최신 대본 섹션 */}
        <section className="mb-12">
          <div className="flex items-center justify-between mb-8">
            <h2 className="text-3xl font-bold text-gray-800 flex items-center">
              <Calendar className="w-8 h-8 text-green-500 mr-3" />
              최신 대본
            </h2>
            <Link
              to="/scripts"
              className="text-primary-600 hover:text-primary-700 font-medium"
            >
              더 보기 →
            </Link>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {latestScripts.map((script) => (
              <ScriptCard key={script._id} script={script} />
            ))}
          </div>
        </section>

        {/* 감정별 대본 찾기 */}
        <section>
          <h2 className="text-3xl font-bold text-gray-800 mb-8">감정별 대본 찾기</h2>
          <div className="grid grid-cols-2 md:grid-cols-5 lg:grid-cols-10 gap-4">
            {emotions.map((emotion) => (
              <Link
                key={emotion._id}
                to={`/scripts?emotion=${encodeURIComponent(emotion.name)}`}
                className="bg-white hover:bg-primary-50 border-2 border-gray-200 hover:border-primary-300 rounded-lg p-4 text-center transition duration-200"
              >
                <span className="text-gray-700 font-medium">#{emotion.name}</span>
              </Link>
            ))}
          </div>
        </section>
      </div>
    </div>
  );
};

// 대본 카드 컴포넌트
const ScriptCard = ({ script }) => {
  return (
    <div className="card p-6">
      <h3 className="text-xl font-semibold mb-3 text-gray-800">
        {script.title}
      </h3>
      
      <div className="flex items-center text-sm text-gray-600 mb-3">
        <Users className="w-4 h-4 mr-1" />
        <span>{script.characterCount}명</span>
        
        <Eye className="w-4 h-4 ml-4 mr-1" />
        <span>{script.views}</span>
        
        <Calendar className="w-4 h-4 ml-4 mr-1" />
        <span>{new Date(script.createdAt).toLocaleDateString('ko-KR')}</span>
      </div>
      
      <div className="mb-4">
        {script.emotions?.map((emotion, index) => (
          <span key={index} className="emotion-tag">
            #{emotion}
          </span>
        ))}
      </div>
      
      <Link
        to={`/scripts/${script._id}`}
        className="btn-primary inline-block text-center w-full"
      >
        자세히 보기
      </Link>
    </div>
  );
};

export default Home;
