import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Search, Users, Eye, Calendar, Heart, BookOpen } from 'lucide-react';
import { scriptAPI, emotionAPI } from '../services/api';

const Home = () => {
  const [popularScripts, setPopularScripts] = useState([]);
  const [latestScripts, setLatestScripts] = useState([]);
  const [emotions, setEmotions] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [popularRes, latestRes, emotionsRes] = await Promise.all([
          scriptAPI.getPopular(),
          scriptAPI.getLatest(),
          emotionAPI.getAll()
        ]);
        
        setPopularScripts(popularRes.data);
        setLatestScripts(latestRes.data);
        setEmotions(emotionsRes.data);
      } catch (error) {
        console.error('데이터 로딩 실패:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/scripts?search=${encodeURIComponent(searchTerm)}`);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto"></div>
          <p className="mt-4 text-secondary">로딩 중...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      {/* 히어로 섹션 */}
      <section className="bg-primary text-white py-16">
        <div className="container text-center">
          <h1 className="text-4xl md:text-5xl font-bold mb-6">
            연기 대본 라이브러리
          </h1>
          <p className="text-xl mb-8 opacity-90">
            다양한 감정과 상황의 연기 대본을 찾아보세요
          </p>
          
          {/* 검색 폼 */}
          <form onSubmit={handleSearch} className="max-w-2xl mx-auto mb-8">
            <div className="flex bg-white rounded-lg overflow-hidden shadow-lg">
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="제목, 상황, 감정으로 검색..."
                className="flex-1 px-6 py-4 text-gray-800 focus:outline-none"
              />
              <button
                type="submit"
                className="bg-gray-800 hover:bg-gray-900 px-8 py-4 transition duration-200"
              >
                <Search className="w-5 h-5" />
              </button>
            </div>
          </form>

          {/* CTA 버튼들 */}
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <Link to="/scripts" className="btn btn-secondary">
              <BookOpen className="w-5 h-5 mr-2" />
              대본 둘러보기
            </Link>
            <Link to="/add-script" className="btn bg-white text-primary hover:bg-gray-100">
              <Heart className="w-5 h-5 mr-2" />
              대본 등록하기
            </Link>
          </div>
        </div>
      </section>

      <div className="container py-16">
        {/* 통계 섹션 */}
        <div className="grid grid-3 gap-8 mb-16">
          <div className="text-center">
            <div className="w-16 h-16 bg-primary bg-opacity-10 rounded-xl flex-center mx-auto mb-4">
              <BookOpen className="w-8 h-8 text-primary" />
            </div>
            <h3 className="text-2xl font-bold text-gray-900 mb-2">1,000+</h3>
            <p className="text-secondary">다양한 대본들</p>
          </div>
          <div className="text-center">
            <div className="w-16 h-16 bg-primary bg-opacity-10 rounded-xl flex-center mx-auto mb-4">
              <Users className="w-8 h-8 text-primary" />
            </div>
            <h3 className="text-2xl font-bold text-gray-900 mb-2">500+</h3>
            <p className="text-secondary">활발한 사용자</p>
          </div>
          <div className="text-center">
            <div className="w-16 h-16 bg-primary bg-opacity-10 rounded-xl flex-center mx-auto mb-4">
              <Heart className="w-8 h-8 text-primary" />
            </div>
            <h3 className="text-2xl font-bold text-gray-900 mb-2">50+</h3>
            <p className="text-secondary">감정 카테고리</p>
          </div>
        </div>

        {/* 인기 대본 & 최신 대본 섹션 */}
        <section className="mb-16">
          <div className="grid grid-2 gap-8">
            {/* 인기 대본 */}
            <div>
              <div className="flex-between mb-6">
                <h2 className="text-2xl font-bold text-gray-900">인기 대본</h2>
                <Link
                  to="/scripts?sort=popular"
                  className="text-primary hover:text-gray-700 text-sm font-medium"
                >
                  더 보기 →
                </Link>
              </div>
              
              <div className="card">
                <div className="space-y-4">
                  {popularScripts.slice(0, 4).map((script) => (
                    <ScriptListItem key={script._id} script={script} />
                  ))}
                </div>
              </div>
            </div>

            {/* 최신 대본 */}
            <div>
              <div className="flex-between mb-6">
                <h2 className="text-2xl font-bold text-gray-900">최신 대본</h2>
                <Link
                  to="/scripts"
                  className="text-primary hover:text-gray-700 text-sm font-medium"
                >
                  더 보기 →
                </Link>
              </div>
              
              <div className="card">
                <div className="space-y-4">
                  {latestScripts.slice(0, 4).map((script) => (
                    <ScriptListItem key={script._id} script={script} />
                  ))}
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* 감정별 대본 찾기 */}
        <section>
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">감정별 대본 찾기</h2>
            <p className="text-secondary max-w-2xl mx-auto">
              원하는 감정의 대본을 빠르게 찾아보세요. 각 감정별로 엄선된 대본들이 준비되어 있습니다.
            </p>
          </div>
          
          <div className="grid grid-4 gap-4">
            {emotions.slice(0, 12).map((emotion) => (
              <Link
                key={emotion._id}
                to={`/scripts?emotion=${encodeURIComponent(emotion.name)}`}
                className="card text-center hover:shadow-lg transition-all duration-200"
              >
                <div className="text-primary font-bold text-lg mb-2">
                  {emotion.name.charAt(0)}
                </div>
                <span className="text-gray-700 font-medium text-sm">
                  #{emotion.name}
                </span>
              </Link>
            ))}
          </div>
          
          {emotions.length > 12 && (
            <div className="text-center mt-8">
              <Link to="/scripts" className="btn btn-outline">
                모든 감정 보기
              </Link>
            </div>
          )}
        </section>
      </div>
    </div>
  );
};

// 대본 리스트 아이템 컴포넌트
const ScriptListItem = ({ script }) => {
  return (
    <Link 
      to={`/scripts/${script._id}`}
      className="block bg-gray-50 hover:bg-gray-100 rounded-lg p-4 transition duration-200"
    >
      <h3 className="font-semibold text-gray-900 mb-2 line-clamp-2">
        {script.title}
      </h3>
      
      <div className="flex items-center text-sm text-secondary mb-3 space-x-4">
        <div className="flex items-center">
          <Users className="w-4 h-4 mr-1" />
          <span>{script.characterCount}명</span>
        </div>
        <div className="flex items-center">
          <Eye className="w-4 h-4 mr-1" />
          <span>{script.views || 0}</span>
        </div>
        <div className="flex items-center">
          <Calendar className="w-4 h-4 mr-1" />
          <span>{new Date(script.createdAt).toLocaleDateString('ko-KR')}</span>
        </div>
      </div>

      <div className="flex flex-wrap gap-1">
        {script.emotions?.slice(0, 3).map((emotion, idx) => (
          <span 
            key={idx} 
            className="inline-block bg-primary bg-opacity-10 text-primary text-xs px-2 py-1 rounded-full"
          >
            #{emotion}
          </span>
        ))}
        {script.emotions?.length > 3 && (
          <span className="inline-block bg-gray-100 text-gray-600 text-xs px-2 py-1 rounded-full">
            +{script.emotions.length - 3}
          </span>
        )}
      </div>
    </Link>
  );
};

export default Home;
