import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Search, Users, Eye, Calendar, Heart } from 'lucide-react';
import { scriptAPI, emotionAPI } from '../services/api';

const Home = () => {
  const [popularScripts, setPopularScripts] = useState([]);
  const [latestScripts, setLatestScripts] = useState([]);
  const [emotions, setEmotions] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [popularRes, latestRes, emotionsRes] = await Promise.all([
          scriptAPI.getPopular(),
          scriptAPI.getLatest(),
          emotionAPI.getAll()
        ]);
        
        setPopularScripts(popularRes.data);
        setLatestScripts(latestRes.data);
        setEmotions(emotionsRes.data);
      } catch (error) {
        console.error('데이터 로딩 실패:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/scripts?search=${encodeURIComponent(searchTerm)}`);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('ko-KR');
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">로딩 중...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* 헤로 섹션 */}
      <section className="bg-gradient-to-r from-primary-600 to-primary-700 text-white py-16">
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-4xl md:text-6xl font-bold mb-6">
            연기 대본 라이브러리
          </h1>
          <p className="text-xl mb-8">
            다양한 감정과 상황의 연기 대본을 찾아보세요
          </p>
          
          {/* 검색 폼 */}
          <form onSubmit={handleSearch} className="max-w-2xl mx-auto">
            <div className="flex bg-white rounded-lg overflow-hidden shadow-lg">
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="제목, 상황, 감정으로 검색..."
                className="flex-1 px-6 py-4 text-gray-800 focus:outline-none"
              />
              <button
                type="submit"
                className="bg-primary-500 hover:bg-primary-600 px-8 py-4 transition duration-200"
              >
                <Search className="w-5 h-5" />
              </button>
            </div>
          </form>
        </div>
      </section>

      <div className="container mx-auto px-4 py-12">
        {/* 인기 대본 & 최신 대본 섹션 */}
        <section className="mb-12">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* 인기 대본 카드 */}
            <div className="bg-white rounded-xl shadow-lg p-6 border border-gray-100">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold text-gray-800 flex items-center">
                  <Heart className="w-7 h-7 text-red-500 mr-3" />
                  인기 대본
                </h2>
                <Link
                  to="/scripts?sort=popular"
                  className="text-primary-600 hover:text-primary-700 font-medium text-sm flex items-center"
                >
                  더 보기 →
                </Link>
              </div>
              
              <div className="space-y-4">
                {popularScripts.slice(0, 4).map((script) => (
                  <ScriptListItem key={script._id} script={script} />
                ))}
              </div>
            </div>

            {/* 최신 대본 카드 */}
            <div className="bg-white rounded-xl shadow-lg p-6 border border-gray-100">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-2xl font-bold text-gray-800 flex items-center">
                  <Calendar className="w-7 h-7 text-green-500 mr-3" />
                  최신 대본
                </h2>
                <Link
                  to="/scripts"
                  className="text-primary-600 hover:text-primary-700 font-medium text-sm flex items-center"
                >
                  더 보기 →
                </Link>
              </div>
              
              <div className="space-y-4">
                {latestScripts.slice(0, 4).map((script) => (
                  <ScriptListItem key={script._id} script={script} />
                ))}
              </div>
            </div>
          </div>
        </section>

        {/* 감정별 대본 찾기 */}
        <section>
          <h2 className="text-3xl font-bold text-gray-800 mb-8">감정별 대본 찾기</h2>
          <div className="grid grid-cols-2 md:grid-cols-5 lg:grid-cols-10 gap-4">
            {emotions.map((emotion) => (
              <Link
                key={emotion._id}
                to={`/scripts?emotion=${encodeURIComponent(emotion.name)}`}
                className="bg-white hover:bg-primary-50 border-2 border-gray-200 hover:border-primary-300 rounded-lg p-4 text-center transition duration-200 shadow-sm"
              >
                <span className="text-gray-700 font-medium">#{emotion.name}</span>
              </Link>
            ))}
          </div>
        </section>
      </div>
    </div>
  );
};

// 대본 리스트 아이템 컴포넌트 (카드 내부용)
const ScriptListItem = ({ script }) => {
  return (
    <Link 
      to={`/scripts/${script._id}`}
      className="block bg-gray-50 hover:bg-gray-100 rounded-lg p-4 transition duration-200 border border-gray-200 hover:border-primary-300"
    >
      <h3 className="text-lg font-semibold mb-2 text-gray-800 truncate">
        {script.title}
      </h3>
      
      <div className="flex items-center text-sm text-gray-600 mb-2">
        <Users className="w-4 h-4 mr-1" />
        <span className="mr-4">{script.characterCount}명</span>
        
        <Eye className="w-4 h-4 mr-1" />
        <span className="mr-4">{script.views}</span>
        
        <Calendar className="w-4 h-4 mr-1" />
        <span>{new Date(script.createdAt).toLocaleDateString('ko-KR')}</span>
      </div>

      <div className="flex flex-wrap gap-1">
        {script.emotions?.slice(0, 3).map((emotion, index) => (
          <span 
            key={index} 
            className="inline-block bg-primary-100 text-primary-700 text-xs px-2 py-1 rounded-full"
          >
            #{emotion}
          </span>
        ))}
        {script.emotions?.length > 3 && (
          <span className="inline-block bg-gray-200 text-gray-600 text-xs px-2 py-1 rounded-full">
            +{script.emotions.length - 3}
          </span>
        )}
      </div>
    </Link>
  );
};

// 대본 카드 컴포넌트 (기존 - 사용하지 않음)
const ScriptCard = ({ script }) => {
  return (
    <div className="card p-6">
      <h3 className="text-xl font-semibold mb-3 text-gray-800">
        {script.title}
      </h3>
      
      <div className="flex items-center text-sm text-gray-600 mb-3">
        <Users className="w-4 h-4 mr-1" />
        <span>{script.characterCount}명</span>
        
        <Eye className="w-4 h-4 ml-4 mr-1" />
        <span>{script.views}</span>
        
        <Calendar className="w-4 h-4 ml-4 mr-1" />
        <span>{new Date(script.createdAt).toLocaleDateString('ko-KR')}</span>
      </div>

      <div className="mb-4">
        {script.emotions?.map((emotion, index) => (
          <span key={index} className="emotion-tag">
            #{emotion}
          </span>
        ))}
      </div>
      
      <Link
        to={`/scripts/${script._id}`}
        className="btn-primary inline-block text-center w-full"
      >
        자세히 보기
      </Link>
    </div>
  );
};

export default Home;
