import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Search, Users, Eye, Calendar, Heart, Sparkles, TrendingUp, Clock, ArrowRight } from 'lucide-react';
import { scriptAPI, emotionAPI } from '../services/api';

const Home = () => {
  const [popularScripts, setPopularScripts] = useState([]);
  const [latestScripts, setLatestScripts] = useState([]);
  const [emotions, setEmotions] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [popularRes, latestRes, emotionsRes] = await Promise.all([
          scriptAPI.getPopular(),
          scriptAPI.getLatest(),
          emotionAPI.getAll()
        ]);
        
        setPopularScripts(popularRes.data);
        setLatestScripts(latestRes.data);
        setEmotions(emotionsRes.data);
      } catch (error) {
        console.error('데이터 로딩 실패:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/scripts?search=${encodeURIComponent(searchTerm)}`);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-4 border-emerald-200 border-t-emerald-600 mx-auto"></div>
          <p className="mt-4 text-emerald-600 font-medium">로딩 중...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      {/* 히어로 섹션 */}
      <section className="relative overflow-hidden bg-gradient-emerald py-20 sm:py-32">
        <div className="absolute inset-0 bg-pattern opacity-10"></div>
        <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <div className="animate-fade-in">
            <div className="flex justify-center mb-6">
              <div className="inline-flex items-center px-4 py-2 bg-white/20 backdrop-blur-lg rounded-full text-white text-sm font-medium">
                <Sparkles className="w-4 h-4 mr-2" />
                한국 최대의 연기 대본 플랫폼
              </div>
            </div>
            
            <h1 className="text-4xl sm:text-5xl lg:text-6xl font-bold text-white mb-6 leading-tight">
              감정을 담은 <br />
              <span className="text-emerald-200">연기 대본</span>을 찾아보세요
            </h1>
            
            <p className="text-xl text-emerald-100 mb-10 max-w-2xl mx-auto leading-relaxed">
              다양한 감정과 상황의 연기 대본으로 당신의 연기 실력을 한 단계 더 발전시켜보세요
            </p>
            
            {/* 검색 폼 */}
            <form onSubmit={handleSearch} className="max-w-2xl mx-auto mb-8">
              <div className="relative">
                <input
                  type="text"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  placeholder="제목, 상황, 감정으로 검색하세요..."
                  className="w-full px-6 py-4 pl-12 text-gray-800 bg-white/95 backdrop-blur-lg rounded-2xl shadow-emerald-lg focus:outline-none focus:ring-4 focus:ring-white/30 text-lg border-2 border-white/20"
                />
                <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-emerald-500 w-5 h-5" />
                <button
                  type="submit"
                  className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-emerald-600 hover:bg-emerald-700 text-white px-6 py-2 rounded-xl font-semibold transition-all duration-200 hover-scale"
                >
                  검색
                </button>
              </div>
            </form>

            {/* CTA 버튼들 */}
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link 
                to="/scripts" 
                className="btn-secondary bg-white/20 border-white/40 text-white hover:bg-white/30 backdrop-blur-lg"
              >
                <Search className="w-5 h-5 mr-2" />
                대본 둘러보기
              </Link>
              <Link 
                to="/add-script" 
                className="bg-white text-emerald-600 hover:text-emerald-700 px-6 py-3 rounded-xl font-semibold shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 transition-all duration-200"
              >
                <Heart className="w-5 h-5 mr-2 inline" />
                대본 등록하기
              </Link>
            </div>
          </div>
        </div>
      </section>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
        {/* 통계 섹션 */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-16">
          <div className="text-center">
            <div className="w-16 h-16 bg-emerald-100 rounded-2xl flex items-center justify-center mx-auto mb-4">
              <Heart className="w-8 h-8 text-emerald-600" />
            </div>
            <h3 className="text-2xl font-bold text-gray-900 mb-2">1,000+</h3>
            <p className="text-gray-600">다양한 대본들</p>
          </div>
          <div className="text-center">
            <div className="w-16 h-16 bg-emerald-100 rounded-2xl flex items-center justify-center mx-auto mb-4">
              <Users className="w-8 h-8 text-emerald-600" />
            </div>
            <h3 className="text-2xl font-bold text-gray-900 mb-2">500+</h3>
            <p className="text-gray-600">활발한 사용자</p>
          </div>
          <div className="text-center">
            <div className="w-16 h-16 bg-emerald-100 rounded-2xl flex items-center justify-center mx-auto mb-4">
              <Sparkles className="w-8 h-8 text-emerald-600" />
            </div>
            <h3 className="text-2xl font-bold text-gray-900 mb-2">50+</h3>
            <p className="text-gray-600">감정 카테고리</p>
          </div>
        </div>

        {/* 인기 대본 & 최신 대본 섹션 */}
        <section className="mb-16">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* 인기 대본 */}
            <div className="animate-fade-in">
              <div className="flex items-center justify-between mb-6">
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 bg-red-100 rounded-xl flex items-center justify-center">
                    <TrendingUp className="w-6 h-6 text-red-600" />
                  </div>
                  <h2 className="text-2xl font-bold text-gray-900">인기 대본</h2>
                </div>
                <Link
                  to="/scripts?sort=popular"
                  className="text-emerald-600 hover:text-emerald-700 font-medium text-sm flex items-center group"
                >
                  더 보기
                  <ArrowRight className="w-4 h-4 ml-1 group-hover:translate-x-1 transition-transform" />
                </Link>
              </div>
              
              <div className="card-featured p-6">
                <div className="space-y-4">
                  {popularScripts.slice(0, 4).map((script, index) => (
                    <ScriptListItem key={script._id} script={script} index={index} />
                  ))}
                </div>
              </div>
            </div>

            {/* 최신 대본 */}
            <div className="animate-fade-in" style={{animationDelay: '0.1s'}}>
              <div className="flex items-center justify-between mb-6">
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 bg-blue-100 rounded-xl flex items-center justify-center">
                    <Clock className="w-6 h-6 text-blue-600" />
                  </div>
                  <h2 className="text-2xl font-bold text-gray-900">최신 대본</h2>
                </div>
                <Link
                  to="/scripts"
                  className="text-emerald-600 hover:text-emerald-700 font-medium text-sm flex items-center group"
                >
                  더 보기
                  <ArrowRight className="w-4 h-4 ml-1 group-hover:translate-x-1 transition-transform" />
                </Link>
              </div>
              
              <div className="card-featured p-6">
                <div className="space-y-4">
                  {latestScripts.slice(0, 4).map((script, index) => (
                    <ScriptListItem key={script._id} script={script} index={index} />
                  ))}
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* 감정별 대본 찾기 */}
        <section className="animate-fade-in" style={{animationDelay: '0.2s'}}>
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">감정별 대본 찾기</h2>
            <p className="text-gray-600 max-w-2xl mx-auto">
              원하는 감정의 대본을 빠르게 찾아보세요. 각 감정별로 엄선된 대본들이 준비되어 있습니다.
            </p>
          </div>
          
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-5 lg:grid-cols-8 gap-3">
            {emotions.slice(0, 16).map((emotion, index) => (
              <Link
                key={emotion._id}
                to={`/scripts?emotion=${encodeURIComponent(emotion.name)}`}
                className="group"
                style={{animationDelay: `${index * 0.05}s`}}
              >
                <div className="card p-4 text-center hover:shadow-emerald transition-all duration-300 group-hover:scale-105">
                  <div className="w-10 h-10 bg-emerald-100 rounded-xl flex items-center justify-center mx-auto mb-3 group-hover:bg-emerald-200 transition-colors">
                    <span className="text-emerald-600 font-bold text-lg">
                      {emotion.name.charAt(0)}
                    </span>
                  </div>
                  <span className="text-gray-700 font-medium text-sm group-hover:text-emerald-700 transition-colors">
                    #{emotion.name}
                  </span>
                </div>
              </Link>
            ))}
          </div>
          
          {emotions.length > 16 && (
            <div className="text-center mt-8">
              <Link
                to="/scripts"
                className="btn-outline"
              >
                모든 감정 보기
              </Link>
            </div>
          )}
        </section>
      </div>
    </div>
  );
};

// 대본 리스트 아이템 컴포넌트
const ScriptListItem = ({ script, index }) => {
  return (
    <Link 
      to={`/scripts/${script._id}`}
      className="block group"
      style={{animationDelay: `${index * 0.05}s`}}
    >
      <div className="bg-white rounded-xl p-4 border border-gray-100 hover:border-emerald-200 hover:shadow-md transition-all duration-200 group-hover:-translate-y-1">
        <h3 className="font-semibold text-gray-900 mb-2 group-hover:text-emerald-700 transition-colors line-clamp-2">
          {script.title}
        </h3>
        
        <div className="flex items-center text-sm text-gray-500 mb-3 space-x-4">
          <div className="flex items-center">
            <Users className="w-4 h-4 mr-1" />
            <span>{script.characterCount}명</span>
          </div>
          <div className="flex items-center">
            <Eye className="w-4 h-4 mr-1" />
            <span>{script.views || 0}</span>
          </div>
          <div className="flex items-center">
            <Calendar className="w-4 h-4 mr-1" />
            <span>{new Date(script.createdAt).toLocaleDateString('ko-KR')}</span>
          </div>
        </div>

        <div className="flex flex-wrap gap-1">
          {script.emotions?.slice(0, 3).map((emotion, idx) => (
            <span 
              key={idx} 
              className="emotion-tag"
            >
              #{emotion}
            </span>
          ))}
          {script.emotions?.length > 3 && (
            <span className="inline-block bg-gray-100 text-gray-600 text-xs px-3 py-1 rounded-full font-medium">
              +{script.emotions.length - 3}
            </span>
          )}
        </div>
      </div>
    </Link>
  );
};

export default Home;
