import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { User, Heart, Settings, Eye, Calendar, Users } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';

const MyPage = () => {
  const { user, isAuthenticated, likedScripts, loadLikedScripts, removeLikedScript } = useAuth();
  const [loading, setLoading] = useState(true);

  // 좋아요한 대본 데이터 로드
  useEffect(() => {
    if (isAuthenticated) {
      loadLikedScripts();
      
      // 더미 데이터가 없으면 추가 (테스트용)
      const stored = localStorage.getItem('likedScripts');
      if (!stored || JSON.parse(stored).length === 0) {
        const dummyLikedScripts = [
          {
            _id: '1',
            title: '첫사랑의 추억',
            characterCount: 2,
            views: 1250,
            emotions: ['그리움', '사랑'],
            createdAt: '2024-01-15',
            likedAt: '2024-01-20'
          },
          {
            _id: '2', 
            title: '이별의 순간',
            characterCount: 2,
            views: 890,
            emotions: ['슬픔', '그리움'],
            createdAt: '2024-01-10',
            likedAt: '2024-01-18'
          },
          {
            _id: '3',
            title: '희망의 메시지',
            characterCount: 1,
            views: 750,
            emotions: ['희망', '기쁨'],
            createdAt: '2024-01-05',
            likedAt: '2024-01-12'
          }
        ];
        localStorage.setItem('likedScripts', JSON.stringify(dummyLikedScripts));
        loadLikedScripts();
      }
      
      setTimeout(() => setLoading(false), 300);
    }
  }, [isAuthenticated, loadLikedScripts]);

  // 좋아요 제거 핸들러
  const handleRemoveLike = (scriptId) => {
    removeLikedScript(scriptId);
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">로그인이 필요합니다</h2>
          <p className="text-gray-600 mb-6">마이페이지를 이용하려면 먼저 로그인해주세요.</p>
          <Link to="/login" className="btn btn-primary">
            로그인하기
          </Link>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="spinner mx-auto"></div>
          <p className="mt-4 text-secondary">로딩 중...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="container mx-auto px-4">
        <div className="max-w-4xl mx-auto">
          
          {/* 페이지 헤더 */}
          <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-8">
            <div className="flex items-center space-x-4">
              <div className="w-16 h-16 bg-gradient-to-br from-emerald-100 to-emerald-200 rounded-full flex items-center justify-center">
                <User className="w-8 h-8 text-emerald-600" />
              </div>
              <div className="flex-1">
                <h1 className="text-2xl font-bold text-gray-900">마이페이지</h1>
                <p className="text-gray-600">안녕하세요, {user?.name || user?.username}님!</p>
              </div>
              <button className="flex items-center space-x-2 text-gray-600 hover:text-emerald-600 px-3 py-2 rounded-lg hover:bg-gray-50">
                <Settings className="w-5 h-5" />
                <span>설정</span>
              </button>
            </div>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            
            {/* 내 정보 섹션 */}
            <div className="lg:col-span-1">
              <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                  <User className="w-5 h-5 mr-2 text-emerald-600" />
                  내 정보
                </h2>
                
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium text-gray-500">이름</label>
                    <p className="text-gray-900">{user?.name || user?.username || '설정되지 않음'}</p>
                  </div>
                  
                  <div>
                    <label className="text-sm font-medium text-gray-500">이메일</label>
                    <p className="text-gray-900">{user?.email || '설정되지 않음'}</p>
                  </div>
                  
                  <div>
                    <label className="text-sm font-medium text-gray-500">가입일</label>
                    <p className="text-gray-900">
                      {user?.createdAt 
                        ? new Date(user.createdAt).toLocaleDateString('ko-KR')
                        : '2024년 1월 1일'
                      }
                    </p>
                  </div>
                  
                  <div>
                    <label className="text-sm font-medium text-gray-500">좋아요한 대본</label>
                    <p className="text-2xl font-bold text-emerald-600">{likedScripts.length}개</p>
                  </div>
                </div>

                <button className="w-full mt-6 px-4 py-2 bg-emerald-50 text-emerald-600 rounded-lg hover:bg-emerald-100 transition-colors">
                  프로필 수정
                </button>
              </div>
            </div>

            {/* 좋아요한 대본 섹션 */}
            <div className="lg:col-span-2">
              <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                <h2 className="text-xl font-semibold text-gray-900 mb-6 flex items-center">
                  <Heart className="w-5 h-5 mr-2 text-red-500" />
                  좋아요한 대본 ({likedScripts.length})
                </h2>
                
                {likedScripts.length === 0 ? (
                  <div className="text-center py-12">
                    <Heart className="w-12 h-12 text-gray-300 mx-auto mb-4" />
                    <p className="text-gray-500 mb-4">아직 좋아요한 대본이 없습니다.</p>
                    <Link to="/scripts" className="text-emerald-600 hover:text-emerald-700 font-medium">
                      대본 둘러보기 →
                    </Link>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {likedScripts.map((script) => (
                      <div key={script._id} className="border border-gray-200 rounded-lg p-4 hover:border-emerald-300 hover:shadow-sm transition-all">
                        <div className="flex items-start justify-between">
                          <div className="flex-1">
                            <h3 className="text-lg font-semibold text-gray-900 mb-2">
                              {script.title}
                            </h3>
                            
                            <div className="flex items-center text-sm text-gray-600 mb-3 flex-wrap gap-4">
                              <div className="flex items-center">
                                <Users className="w-4 h-4 mr-1" />
                                <span>{script.characterCount}명</span>
                              </div>
                              
                              <div className="flex items-center">
                                <Eye className="w-4 h-4 mr-1" />
                                <span>{script.views}</span>
                              </div>
                              
                              <div className="flex items-center">
                                <Calendar className="w-4 h-4 mr-1" />
                                <span>{new Date(script.createdAt).toLocaleDateString('ko-KR')}</span>
                              </div>
                            </div>
                            
                            <div className="flex flex-wrap gap-2 mb-3">
                              {script.emotions?.map((emotion, index) => (
                                <span key={index} className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-emerald-100 text-emerald-800">
                                  #{emotion}
                                </span>
                              ))}
                            </div>
                            
                            <p className="text-xs text-gray-500">
                              {new Date(script.likedAt).toLocaleDateString('ko-KR')}에 좋아요 추가
                            </p>
                          </div>
                          
                          <div className="flex items-center space-x-2 ml-4">
                            <button className="p-2 text-red-500 hover:bg-red-50 rounded-lg">
                              <Heart className="w-4 h-4 fill-current" />
                            </button>
                            <Link 
                              to={`/scripts/${script._id}`}
                              className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 text-sm font-medium transition-colors"
                            >
                              보기
                            </Link>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MyPage;
