sourcesContent:
import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Sparkles, 
  Users, 
  Palette, 
  Heart, 
  Clock, 
  Wand2, 
  Copy, 
  Save, 
  RefreshCw,
  ChevronDown,
  X,
  MapPin,
  Film,
  PenTool,
  Edit3,
  ArrowRight,
  Check,
  RotateCcw,
  Eye,
  Maximize2
} from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';

const AIScript = () => {
  const { addAIGeneratedScript } = useAuth();
  
  // Ìèº ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [formData, setFormData] = useState({
    characterCount: '',
    genre: '',
    emotions: [],
    length: '',
    situation: '',
    style: '',
    location: ''
  });

  const [isGenerating, setIsGenerating] = useState(false);
  const [generatedScript, setGeneratedScript] = useState('');
  const [error, setError] = useState('');
  const [showGenreDropdown, setShowGenreDropdown] = useState(false);
  const [showStyleDropdown, setShowStyleDropdown] = useState(false);
  const [showLocationDropdown, setShowLocationDropdown] = useState(false);
  
  // Î¶¨ÎùºÏù¥ÌåÖ Í¥ÄÎ†® ÏÉÅÌÉú
  const [selectedText, setSelectedText] = useState('');
  const [selectedTextStart, setSelectedTextStart] = useState(0);
  const [selectedTextEnd, setSelectedTextEnd] = useState(0);
  const [showRewriteModal, setShowRewriteModal] = useState(false);
  const [rewriteIntensity, setRewriteIntensity] = useState('');
  const [isRewriting, setIsRewriting] = useState(false);
  const [rewriteResult, setRewriteResult] = useState(null);
  
  // ÏÉÅÏÑ∏ Î≥¥Í∏∞ Î™®Îã¨ ÏÉÅÌÉú
  const [showDetailModal, setShowDetailModal] = useState(false);

  // ÏòµÏÖò Îç∞Ïù¥ÌÑ∞
  const characterOptions = [
    { value: '1', label: '1Ïù∏ ÎèÖÎ∞±', icon: 'üë§' },
    { value: '2-3', label: '2~3Ïù∏ ÎåÄÌôî', icon: 'üë•' },
    { value: '4+', label: '4Ïù∏ Ïù¥ÏÉÅ ÏïôÏÉÅÎ∏î', icon: 'üë®‚Äçüë©‚Äçüëß‚Äçüë¶' }
  ];

  const genres = [
    'Î°úÎß®Ïä§', 'ÏΩîÎØ∏Îîî', 'Ïä§Î¶¥Îü¨', 'ÎìúÎùºÎßà', 'Ïï°ÏÖò', 
    'Í≥µÌè¨', 'ÌåêÌÉÄÏßÄ', 'SF', 'ÎØ∏Ïä§ÌÑ∞Î¶¨', 'ÏãúÎåÄÍ∑π'
  ];

  const emotions = [
    'Í∏∞ÏÅ®', 'Ïä¨Ìîî', 'Î∂ÑÎÖ∏', 'ÏÇ¨Îûë', 'Í∑∏Î¶¨ÏõÄ', 
    'Ìù¨Îßù', 'Ï†àÎßù', 'ÎÜÄÎùºÏõÄ', 'ÎëêÎ†§ÏõÄ', 'ÌèâÏò®'
  ];

  const lengths = [
    { value: 'short', label: 'ÏßßÍ≤å', time: '1~2Î∂Ñ', icon: '‚ö°' },
    { value: 'medium', label: 'Ï§ëÍ∞Ñ', time: '3~5Î∂Ñ', icon: '‚è±Ô∏è' },
    { value: 'long', label: 'Í∏∏Í≤å', time: '5~10Î∂Ñ', icon: 'üìù' }
  ];

  const styles = [
    'ÏõπÎìúÎùºÎßà', 'Î¶¨ÏñºÎ¶¨Ï¶ò', 'Ïó∞Í∑πÌÜ§', 'ÏãúÌä∏ÏΩ§', 'ÏòÅÌôîÌÜ§'
  ];

  const locations = [
    'Î≥ëÏõê', 'Ïπ¥Ìéò', 'Í±∞Î¶¨', 'Í≤ΩÏ∞∞ÏÑú', 'ÌïôÍµê', 'Ïßë', 'ÏÇ¨Î¨¥Ïã§', 'Í≥µÏõê', 'Î≤ÑÏä§', 'ÏßÄÌïòÏ≤†'
  ];

  // Ìèº Îç∞Ïù¥ÌÑ∞ Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨
  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  // Í∞êÏ†ï ÌÜ†Í∏Ä Ìï∏Îì§Îü¨
  const toggleEmotion = (emotion) => {
    setFormData(prev => ({
      ...prev,
      emotions: prev.emotions.includes(emotion)
        ? prev.emotions.filter(e => e !== emotion)
        : [...prev.emotions, emotion]
    }));
  };

  // ÌÖçÏä§Ìä∏ ÏÑ†ÌÉù Ìï∏Îì§Îü¨
  const handleTextSelection = () => {
    const selection = window.getSelection();
    const selected = selection.toString().trim();
    
    if (selected && selected.length > 5) { // ÏµúÏÜå 5Ïûê Ïù¥ÏÉÅ ÏÑ†ÌÉù
      // ÏÑ†ÌÉùÎêú ÌÖçÏä§Ìä∏Î•º ÏõêÎ≥∏ Ïä§ÌÅ¨Î¶ΩÌä∏ÏóêÏÑú Ï∞æÍ∏∞ (Ïó¨Îü¨ Í∞ÄÎä•Ìïú ÏúÑÏπò Í≥†Î†§)
      const scriptText = generatedScript;
      let startIndex = scriptText.indexOf(selected);
      
      // ÎßåÏïΩ Ï∞æÏßÄ Î™ªÌñàÎã§Î©¥, Í≥µÎ∞±Ïù¥ÎÇò ÌäπÏàòÎ¨∏Ïûê Ï∞®Ïù¥ ÎïåÎ¨∏Ïùº Ïàò ÏûàÏúºÎØÄÎ°ú Ï†ïÍ∑úÌôî ÌõÑ Ïû¨ÏãúÎèÑ
      if (startIndex === -1) {
        const normalizedSelected = selected.replace(/\s+/g, ' ').trim();
        const normalizedScript = scriptText.replace(/\s+/g, ' ');
        const normalizedIndex = normalizedScript.indexOf(normalizedSelected);
        
        if (normalizedIndex !== -1) {
          // Ï†ïÍ∑úÌôîÎêú ÏúÑÏπòÎ•º ÏõêÎ≥∏ ÌÖçÏä§Ìä∏ ÏúÑÏπòÎ°ú Î≥ÄÌôò
          let charCount = 0;
          for (let i = 0; i < scriptText.length; i++) {
            if (charCount === normalizedIndex) {
              startIndex = i;
              break;
            }
            if (!/\s/.test(scriptText[i]) || scriptText[i] === ' ') {
              charCount++;
            }
          }
        }
      }
      
      if (startIndex !== -1) {
        const endIndex = startIndex + selected.length;
        setSelectedText(selected);
        setSelectedTextStart(startIndex);
        setSelectedTextEnd(endIndex);
        setShowRewriteModal(true);
        setRewriteIntensity('');
        setRewriteResult(null);
      } else {
        alert('ÏÑ†ÌÉùÎêú ÌÖçÏä§Ìä∏Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§. Îã§Ïãú ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      }
    }
  };

  // Î¶¨ÎùºÏù¥ÌåÖ API Ìò∏Ï∂ú
  const handleRewrite = async () => {
    if (!selectedText || !rewriteIntensity) {
      alert('ÌÖçÏä§Ìä∏Í∞Ä ÏÑ†ÌÉùÎêòÏßÄ ÏïäÏïòÍ±∞ÎÇò Î¶¨ÎùºÏù¥ÌåÖ Í∞ïÎèÑÎ•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }

    setIsRewriting(true);

    try {
      // ÏÑ†ÌÉùÎêú ÌÖçÏä§Ìä∏ Ï£ºÎ≥ÄÏùò Îß•ÎùΩ Ï∂îÏ∂ú (ÏïûÎí§ 200ÏûêÏî©)
      const contextBefore = generatedScript.substring(Math.max(0, selectedTextStart - 200), selectedTextStart);
      const contextAfter = generatedScript.substring(selectedTextEnd, Math.min(generatedScript.length, selectedTextEnd + 200));
      const fullContext = contextBefore + "[ÏÑ†ÌÉùÎêú Î∂ÄÎ∂Ñ: " + selectedText + "]" + contextAfter;
      
      const response = await fetch('http://localhost:5000/api/ai-script/rewrite', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          selectedText,
          intensity: rewriteIntensity,
          context: fullContext,
          fullScript: generatedScript,
          emotion: formData.emotions.join(', '),
          genre: formData.genre
        })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Î¶¨ÎùºÏù¥ÌåÖÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }

      setRewriteResult(data);

    } catch (error) {
      console.error('Î¶¨ÎùºÏù¥ÌåÖ Ïò§Î•ò:', error);
      alert(error.message || 'Î¶¨ÎùºÏù¥ÌåÖ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setIsRewriting(false);
    }
  };

  // Î¶¨ÎùºÏù¥ÌåÖ Í≤∞Í≥º Ï†ÅÏö©
  const applyRewrite = () => {
    if (!rewriteResult) return;

    // Ï†ïÌôïÌïú ÏúÑÏπòÏóêÏÑú ÌÖçÏä§Ìä∏ ÍµêÏ≤¥
    const beforeText = generatedScript.substring(0, selectedTextStart);
    const afterText = generatedScript.substring(selectedTextEnd);
    const newScript = beforeText + rewriteResult.rewritten + afterText;
    
    setGeneratedScript(newScript);
    setShowRewriteModal(false);
    setSelectedText('');
    setSelectedTextStart(0);
    setSelectedTextEnd(0);
    setRewriteResult(null);
    setRewriteIntensity('');
  };

  // Î¶¨ÎùºÏù¥ÌåÖ Î™®Îã¨ Îã´Í∏∞
  // ÎåÄÎ≥∏ ÌååÏã± Î∞è Î†åÎçîÎßÅ Ìï®Ïàò
  const parseAndRenderScript = (script) => {
    if (!script) return null;

    const lines = script.split('\n');
    const sections = [];
    let currentSection = { type: 'text', content: [] };

    lines.forEach((line, index) => {
      const trimmedLine = line.trim();
      
      // ÏÑπÏÖò Ìó§Îçî Í∞êÏßÄ
      if (trimmedLine.match(/^\*\*Ï†úÎ™©:\*\*/i) || trimmedLine.match(/^Ï†úÎ™©:/i)) {
        if (currentSection.content.length > 0) sections.push(currentSection);
        currentSection = { type: 'title', content: [line] };
      } else if (trimmedLine.match(/^\*\*ÏÉÅÌô©[ ]?ÏÑ§Î™Ö:\*\*/i) || trimmedLine.match(/^ÏÉÅÌô©[ ]?ÏÑ§Î™Ö:/i) || trimmedLine.match(/^\[ÏÉÅÌô©[ ]?ÏÑ§Î™Ö\]/i)) {
        if (currentSection.content.length > 0) sections.push(currentSection);
        currentSection = { type: 'situation', content: [line] };
      } else if (trimmedLine.match(/^\*\*Ïù∏Î¨º:\*\*/i) || trimmedLine.match(/^Ïù∏Î¨º:/i) || trimmedLine.match(/^\[Îì±Ïû•Ïù∏Î¨º\]/i) || trimmedLine.match(/^\[Ïù∏Î¨º\]/i)) {
        if (currentSection.content.length > 0) sections.push(currentSection);
        currentSection = { type: 'character', content: [line] };
      } else if (trimmedLine.match(/^\*\*ÎèÖÎ∞±:\*\*/i) || trimmedLine.match(/^ÎèÖÎ∞±:/i) || trimmedLine.match(/^\[ÎèÖÎ∞±\]/i) || trimmedLine.match(/^\*\*ÎåÄÎ≥∏:\*\*/i) || trimmedLine.match(/^ÎåÄÎ≥∏:/i) || trimmedLine.match(/^\[ÎåÄÎ≥∏\]/i)) {
        if (currentSection.content.length > 0) sections.push(currentSection);
        currentSection = { type: 'dialogue', content: [line] };
      } else if (trimmedLine.match(/^\*\*Ïó∞Í∏∞[ ]?ÌåÅ:\*\*/i) || trimmedLine.match(/^Ïó∞Í∏∞[ ]?ÌåÅ:/i) || trimmedLine.match(/^\*\*Ïó∞Í∏∞[ ]?Ìè¨Ïù∏Ìä∏:\*\*/i)) {
        if (currentSection.content.length > 0) sections.push(currentSection);
        currentSection = { type: 'tips', content: [line] };
      } else {
        currentSection.content.push(line);
      }
    });

    if (currentSection.content.length > 0) sections.push(currentSection);

    return sections.map((section, index) => {
      const content = section.content.join('\n');
      
      switch (section.type) {
        case 'title':
          return (
            <div key={index} className="mb-6">
              <div className="text-2xl font-bold text-center text-purple-900 bg-gradient-to-r from-purple-100 to-pink-100 py-4 px-6 rounded-xl border-l-4 border-purple-500">
                {content.replace(/^\*\*Ï†úÎ™©:\*\*\s*/i, '').replace(/^Ï†úÎ™©:\s*/i, '')}
              </div>
            </div>
          );
        case 'situation':
          return (
            <div key={index} className="mb-6">
              <div className="bg-blue-50 border-l-4 border-blue-400 p-4 rounded-r-lg">
                <h3 className="font-bold text-blue-800 mb-2 flex items-center">
                  <span className="w-2 h-2 bg-blue-500 rounded-full mr-2"></span>
                  ÏÉÅÌô© ÏÑ§Î™Ö
                </h3>
                <div className="text-blue-700 leading-relaxed whitespace-pre-wrap font-medium">
                  {content.replace(/^\*\*ÏÉÅÌô©[ ]?ÏÑ§Î™Ö:\*\*\s*/i, '').replace(/^ÏÉÅÌô©[ ]?ÏÑ§Î™Ö:\s*/i, '').replace(/^\[ÏÉÅÌô©[ ]?ÏÑ§Î™Ö\]\s*/i, '')}
                </div>
              </div>
            </div>
          );
        case 'character':
          return (
            <div key={index} className="mb-6">
              <div className="bg-green-50 border-l-4 border-green-400 p-4 rounded-r-lg">
                <h3 className="font-bold text-green-800 mb-2 flex items-center">
                  <span className="w-2 h-2 bg-green-500 rounded-full mr-2"></span>
                  Îì±Ïû•Ïù∏Î¨º
                </h3>
                <div className="text-green-700 leading-relaxed whitespace-pre-wrap font-medium">
                  {content.replace(/^\*\*Ïù∏Î¨º:\*\*\s*/i, '').replace(/^Ïù∏Î¨º:\s*/i, '').replace(/^\[Îì±Ïû•Ïù∏Î¨º\]\s*/i, '').replace(/^\[Ïù∏Î¨º\]\s*/i, '')}
                </div>
              </div>
            </div>
          );
        case 'dialogue':
          return (
            <div key={index} className="mb-6">
              <div className="bg-gray-50 border-l-4 border-gray-400 p-6 rounded-r-lg">
                <h3 className="font-bold text-gray-800 mb-4 flex items-center">
                  <span className="w-2 h-2 bg-gray-500 rounded-full mr-2"></span>
                  {section.content[0].includes('ÎèÖÎ∞±') ? 'ÎèÖÎ∞±' : 'ÎåÄÎ≥∏'}
                </h3>
                <div className="bg-white p-4 rounded-lg border border-gray-200 shadow-sm">
                  <div className="text-gray-800 leading-relaxed whitespace-pre-wrap font-serif text-base">
                    {content.replace(/^\*\*ÎèÖÎ∞±:\*\*\s*/i, '').replace(/^ÎèÖÎ∞±:\s*/i, '').replace(/^\[ÎèÖÎ∞±\]\s*/i, '').replace(/^\*\*ÎåÄÎ≥∏:\*\*\s*/i, '').replace(/^ÎåÄÎ≥∏:\s*/i, '').replace(/^\[ÎåÄÎ≥∏\]\s*/i, '')}
                  </div>
                </div>
              </div>
            </div>
          );
        case 'tips':
          return (
            <div key={index} className="mb-6">
              <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 rounded-r-lg">
                <h3 className="font-bold text-yellow-800 mb-2 flex items-center">
                  <span className="w-2 h-2 bg-yellow-500 rounded-full mr-2"></span>
                  Ïó∞Í∏∞ ÌåÅ
                </h3>
                <div className="text-yellow-700 leading-relaxed whitespace-pre-wrap font-medium">
                  {content.replace(/^\*\*Ïó∞Í∏∞[ ]?ÌåÅ:\*\*\s*/i, '').replace(/^Ïó∞Í∏∞[ ]?ÌåÅ:\s*/i, '').replace(/^\*\*Ïó∞Í∏∞[ ]?Ìè¨Ïù∏Ìä∏:\*\*\s*/i, '')}
                </div>
              </div>
            </div>
          );
        default:
          return (
            <div key={index} className="mb-4">
              <div className="text-gray-700 leading-relaxed whitespace-pre-wrap">
                {content}
              </div>
            </div>
          );
      }
    });
  };

  const closeRewriteModal = () => {
    setShowRewriteModal(false);
    setSelectedText('');
    setSelectedTextStart(0);
    setSelectedTextEnd(0);
    setRewriteResult(null);
    setRewriteIntensity('');
    window.getSelection().removeAllRanges();
  };

  // ÎåÄÎ≥∏ ÏÉùÏÑ± Ìï∏Îì§Îü¨
  const handleGenerate = async (e) => {
    e.preventDefault();
    
    // ÏûÖÎ†•Í∞í Í≤ÄÏ¶ù
    if (!formData.characterCount || !formData.genre || formData.emotions.length === 0 || !formData.length) {
      setError('ÌïÑÏàò Ìï≠Î™©ÏùÑ Î™®Îëê ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî. (Îì±Ïû•Ïù∏Î¨º Ïàò, Ïû•Î•¥, Ï£ºÏöî Í∞êÏ†ï, ÎåÄÎ≥∏ Í∏∏Ïù¥)');
      return;
    }

    setIsGenerating(true);
    setError('');
    setGeneratedScript('');

    try {
      const response = await fetch('http://localhost:5000/api/ai-script/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          characterCount: formData.characterCount,
          genre: formData.genre,
          emotion: formData.emotions.join(', '),
          length: formData.length,
          situation: formData.situation,
          style: formData.style,
          location: formData.location
        })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'ÎåÄÎ≥∏ ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }

      setGeneratedScript(data.script);
      
      // AuthContextÏóê AI ÏÉùÏÑ± ÎåÄÎ≥∏ Ï†ÄÏû•
      addAIGeneratedScript(data);
      
      // Í≤∞Í≥º ÏòÅÏó≠ÏúºÎ°ú Ïä§ÌÅ¨Î°§
      setTimeout(() => {
        const resultElement = document.getElementById('result');
        if (resultElement) {
          resultElement.scrollIntoView({ behavior: 'smooth' });
        }
      }, 100);

    } catch (error) {
      console.error('ÎåÄÎ≥∏ ÏÉùÏÑ± Ïò§Î•ò:', error);
      setError(error.message || 'ÎåÄÎ≥∏ ÏÉùÏÑ± Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setIsGenerating(false);
    }
  };

  // ÎìúÎ°≠Îã§Ïö¥ Ïª¥Ìè¨ÎÑåÌä∏
  const Dropdown = ({ options, value, onChange, placeholder, isOpen, setIsOpen }) => (
    <div className="relative">
      <button
        type="button"
        onClick={() => setIsOpen(!isOpen)}
        className="w-full px-4 py-3 bg-white border border-gray-200 rounded-xl focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-colors flex items-center justify-between hover:border-gray-300"
      >
        <span className={value ? 'text-gray-900' : 'text-gray-500'}>
          {value || placeholder}
        </span>
        <ChevronDown className={`w-5 h-5 transition-transform ${isOpen ? 'rotate-180' : ''}`} />
      </button>
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            className="absolute top-full left-0 right-0 mt-1 bg-white border border-gray-200 rounded-xl shadow-lg z-10 max-h-60 overflow-y-auto"
          >
            {options.map((option) => (
              <button
                key={option}
                type="button"
                onClick={() => {
                  onChange(option);
                  setIsOpen(false);
                }}
                className="w-full px-4 py-3 text-left hover:bg-gray-50 transition-colors first:rounded-t-xl last:rounded-b-xl"
              >
                {option}
              </button>
            ))}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-purple-50 to-pink-50 py-12">
      <div className="container mx-auto px-4">
        <div className="max-w-4xl mx-auto">
          
          {/* ÌéòÏù¥ÏßÄ Ìó§Îçî */}
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="text-center mb-12"
          >
            <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-r from-purple-500 to-pink-500 rounded-2xl mb-6 shadow-lg">
              <Sparkles className="w-10 h-10 text-white" />
            </div>
            <h1 className="text-4xl md:text-5xl font-bold text-gray-900 mb-4">
              AI ÎåÄÎ≥∏ ÏÉùÏÑ±Í∏∞
            </h1>
            <p className="text-xl text-gray-600 max-w-2xl mx-auto leading-relaxed">
              Î™á Í∞ÄÏßÄ ÏÑ§Ï†ïÎßåÏúºÎ°ú ÎãπÏã†ÎßåÏùò ÌäπÎ≥ÑÌïú Ïó∞Í∏∞ ÎåÄÎ≥∏ÏùÑ ÏÉùÏÑ±ÌïòÏÑ∏Ïöî
            </p>
          </motion.div>

          {/* Î©îÏù∏ Ìèº Ïπ¥Îìú */}
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.1 }}
            className="bg-white rounded-2xl shadow-md border border-gray-100 p-8 mb-8"
          >
