import React, { createContext, useContext, useReducer, useEffect } from 'react';
import { authAPI } from '../services/api';

// 초기 상태
const initialState = {
  user: null,
  token: null,
  isAuthenticated: false,
  loading: true,
  error: null,
  likedScripts: [] // 좋아요한 대본 목록
};

// 액션 타입
const AUTH_ACTIONS = {
  LOGIN_START: 'LOGIN_START',
  LOGIN_SUCCESS: 'LOGIN_SUCCESS',
  LOGIN_FAIL: 'LOGIN_FAIL',
  LOGOUT: 'LOGOUT',
  LOAD_USER: 'LOAD_USER',
  SET_LOADING: 'SET_LOADING',
  CLEAR_ERROR: 'CLEAR_ERROR',
  UPDATE_USER: 'UPDATE_USER',
  ADD_LIKED_SCRIPT: 'ADD_LIKED_SCRIPT',
  REMOVE_LIKED_SCRIPT: 'REMOVE_LIKED_SCRIPT',
  SET_LIKED_SCRIPTS: 'SET_LIKED_SCRIPTS'
};

// 리듀서 함수
const authReducer = (state, action) => {
  switch (action.type) {
    case AUTH_ACTIONS.SET_LOADING:
      return {
        ...state,
        loading: action.payload
      };

    case AUTH_ACTIONS.LOGIN_START:
      return {
        ...state,
        loading: true,
        error: null
      };

    case AUTH_ACTIONS.LOGIN_SUCCESS:
      // 토큰을 localStorage에 저장
      if (action.payload.token) {
        localStorage.setItem('token', action.payload.token);
      }
      return {
        ...state,
        user: action.payload.user,
        token: action.payload.token,
        isAuthenticated: true,
        loading: false,
        error: null
      };

    case AUTH_ACTIONS.LOGIN_FAIL:
      localStorage.removeItem('token');
      return {
        ...state,
        user: null,
        token: null,
        isAuthenticated: false,
        loading: false,
        error: action.payload
      };

    case AUTH_ACTIONS.LOGOUT:
      localStorage.removeItem('token');
      return {
        ...state,
        user: null,
        token: null,
        isAuthenticated: false,
        loading: false,
        error: null
      };

    case AUTH_ACTIONS.LOAD_USER:
      return {
        ...state,
        user: action.payload,
        isAuthenticated: true,
        loading: false
      };

    case AUTH_ACTIONS.UPDATE_USER:
      return {
        ...state,
        user: action.payload
      };

    case AUTH_ACTIONS.CLEAR_ERROR:
      return {
        ...state,
        error: null
      };

    case AUTH_ACTIONS.ADD_LIKED_SCRIPT:
      return {
        ...state,
        likedScripts: [...state.likedScripts, action.payload]
      };

    case AUTH_ACTIONS.REMOVE_LIKED_SCRIPT:
      return {
        ...state,
        likedScripts: state.likedScripts.filter(script => script._id !== action.payload)
      };

    case AUTH_ACTIONS.SET_LIKED_SCRIPTS:
      return {
        ...state,
        likedScripts: action.payload
      };

    default:
      return state;
  }
};

// Context 생성
const AuthContext = createContext();

// Context Provider 컴포넌트
export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  // 앱 시작 시 토큰 확인 및 사용자 정보 로드
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      loadUser();
    } else {
      dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: false });
    }
  }, []);

  // 사용자 정보 로드
  const loadUser = async () => {
    try {
      dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: true });
      
      const token = localStorage.getItem('token');
      if (!token) {
        dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: false });
        return;
      }

      // 백엔드가 없으므로 더미 사용자 정보 로드
      if (token.startsWith('dummy-jwt-token')) {
        const dummyUser = {
          _id: 'user123',
          name: '김연기',
          username: 'actor_kim',
          email: 'test@example.com',
          createdAt: '2024-01-01T00:00:00.000Z'
        };
        
        dispatch({
          type: AUTH_ACTIONS.LOAD_USER,
          payload: dummyUser
        });
      } else {
        // 유효하지 않은 토큰
        localStorage.removeItem('token');
        dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: false });
      }
      
      // 실제 API 호출 (백엔드 연동 시 사용)
      // const response = await authAPI.getMe();
      // dispatch({
      //   type: AUTH_ACTIONS.LOAD_USER,
      //   payload: response.data.user
      // });
      
    } catch (error) {
      console.error('사용자 정보 로드 실패:', error);
      localStorage.removeItem('token');
      dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: false });
    }
  };

  // 회원가입
  const register = async (userData) => {
    try {
      dispatch({ type: AUTH_ACTIONS.LOGIN_START });
      
      // 백엔드가 없으므로 더미 회원가입 로직 사용
      const dummyUser = {
        _id: 'user_' + Date.now(),
        name: userData.name,
        username: userData.username || userData.name,
        email: userData.email,
        createdAt: new Date().toISOString()
      };
      
      const dummyToken = 'dummy-jwt-token-' + Date.now();
      
      dispatch({
        type: AUTH_ACTIONS.LOGIN_SUCCESS,
        payload: {
          user: dummyUser,
          token: dummyToken
        }
      });

      return { success: true, message: '회원가입이 완료되었습니다!' };
      
      // 실제 API 호출 (백엔드 연동 시 사용)
      // const response = await authAPI.register(userData);
      // dispatch({
      //   type: AUTH_ACTIONS.LOGIN_SUCCESS,
      //   payload: {
      //     user: response.data.user,
      //     token: response.data.token
      //   }
      // });
      // return { success: true, message: response.data.message };
      
    } catch (error) {
      const errorMessage = error.response?.data?.message || '회원가입 중 오류가 발생했습니다.';
      dispatch({
        type: AUTH_ACTIONS.LOGIN_FAIL,
        payload: errorMessage
      });
      return { success: false, message: errorMessage };
    }
  };

  // 로그인
  const login = async (loginData) => {
    try {
      dispatch({ type: AUTH_ACTIONS.LOGIN_START });
      
      // 백엔드가 없으므로 더미 로그인 로직 사용
      // 더미 사용자 계정: test@example.com / password123
      if (loginData.email === 'test@example.com' && loginData.password === 'password123') {
        const dummyUser = {
          _id: 'user123',
          name: '김연기',
          username: 'actor_kim',
          email: 'test@example.com',
          createdAt: '2024-01-01T00:00:00.000Z'
        };
        
        const dummyToken = 'dummy-jwt-token-' + Date.now();
        
        dispatch({
          type: AUTH_ACTIONS.LOGIN_SUCCESS,
          payload: {
            user: dummyUser,
            token: dummyToken
          }
        });

        return { success: true, message: '로그인 성공!' };
      } else {
        // 잘못된 로그인 정보
        dispatch({
          type: AUTH_ACTIONS.LOGIN_FAIL,
          payload: '이메일 또는 비밀번호가 올바르지 않습니다.'
        });
        return { success: false, message: '이메일 또는 비밀번호가 올바르지 않습니다.' };
      }
      
      // 실제 API 호출 (백엔드 연동 시 사용)
      // const response = await authAPI.login(loginData);
      // dispatch({
      //   type: AUTH_ACTIONS.LOGIN_SUCCESS,
      //   payload: {
      //     user: response.data.user,
      //     token: response.data.token
      //   }
      // });
      // return { success: true, message: response.data.message };
      
    } catch (error) {
      const errorMessage = error.response?.data?.message || '로그인 중 오류가 발생했습니다.';
      dispatch({
        type: AUTH_ACTIONS.LOGIN_FAIL,
        payload: errorMessage
      });
      return { success: false, message: errorMessage };
    }
  };

  // 로그아웃
  const logout = async () => {
    try {
      await authAPI.logout();
    } catch (error) {
      console.error('로그아웃 API 호출 실패:', error);
    } finally {
      dispatch({ type: AUTH_ACTIONS.LOGOUT });
    }
  };

  // 프로필 업데이트
  const updateProfile = async (profileData) => {
    try {
      const response = await authAPI.updateProfile(profileData);
      
      dispatch({
        type: AUTH_ACTIONS.UPDATE_USER,
        payload: response.data.user
      });

      return { success: true, message: response.data.message };
    } catch (error) {
      const errorMessage = error.response?.data?.message || '프로필 수정 중 오류가 발생했습니다.';
      return { success: false, message: errorMessage };
    }
  };

  // 비밀번호 변경
  const changePassword = async (passwordData) => {
    try {
      const response = await authAPI.changePassword(passwordData);
      return { success: true, message: response.data.message };
    } catch (error) {
      const errorMessage = error.response?.data?.message || '비밀번호 변경 중 오류가 발생했습니다.';
      return { success: false, message: errorMessage };
    }
  };

  // 에러 클리어
  const clearError = () => {
    dispatch({ type: AUTH_ACTIONS.CLEAR_ERROR });
  };

  // 대본 좋아요 추가
  const addLikedScript = (script) => {
    // 로컬 스토리지에 저장 (추후 API 연동 예정)
    const currentLiked = JSON.parse(localStorage.getItem('likedScripts') || '[]');
    const newLiked = [...currentLiked, { ...script, likedAt: new Date().toISOString() }];
    localStorage.setItem('likedScripts', JSON.stringify(newLiked));
    
    dispatch({
      type: AUTH_ACTIONS.ADD_LIKED_SCRIPT,
      payload: { ...script, likedAt: new Date().toISOString() }
    });
  };

  // 대본 좋아요 제거
  const removeLikedScript = (scriptId) => {
    // 로컬 스토리지에서 제거 (추후 API 연동 예정)
    const currentLiked = JSON.parse(localStorage.getItem('likedScripts') || '[]');
    const newLiked = currentLiked.filter(script => script._id !== scriptId);
    localStorage.setItem('likedScripts', JSON.stringify(newLiked));
    
    dispatch({
      type: AUTH_ACTIONS.REMOVE_LIKED_SCRIPT,
      payload: scriptId
    });
  };

  // 좋아요한 대본 목록 로드
  const loadLikedScripts = () => {
    // 로컬 스토리지에서 로드 (추후 API 연동 예정)
    const likedScripts = JSON.parse(localStorage.getItem('likedScripts') || '[]');
    dispatch({
      type: AUTH_ACTIONS.SET_LIKED_SCRIPTS,
      payload: likedScripts
    });
  };

  // 대본이 좋아요 되었는지 확인
  const isScriptLiked = (scriptId) => {
    return state.likedScripts.some(script => script._id === scriptId);
  };

  const value = {
    ...state,
    register,
    login,
    logout,
    loadUser,
    updateProfile,
    changePassword,
    clearError,
    addLikedScript,
    removeLikedScript,
    loadLikedScripts,
    isScriptLiked
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

// Context 사용을 위한 커스텀 훅
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export default AuthContext;
