import React, { useState } from 'react';
import { Sparkles, Users, Palette, Heart, Clock, Wand2 } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';

const AIScript = () => {
  const { addAIGeneratedScript } = useAuth();
  const [formData, setFormData] = useState({
    characterCount: '',
    genre: '',
    emotion: '',
    length: ''
  });

  const [isGenerating, setIsGenerating] = useState(false);
  const [generatedScript, setGeneratedScript] = useState('');
  const [error, setError] = useState('');

  // 폼 데이터 변경 핸들러
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // 대본 생성 핸들러
  const handleGenerate = async (e) => {
    e.preventDefault();
    
    // 입력값 검증
    if (!formData.characterCount || !formData.genre || !formData.emotion || !formData.length) {
      alert('모든 항목을 선택해주세요.');
      return;
    }

    setIsGenerating(true);
    setError('');
    setGeneratedScript('');

    try {
      const response = await fetch('http://localhost:5000/api/ai-script/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || '대본 생성에 실패했습니다.');
      }

      setGeneratedScript(data.script);
      
      // AuthContext에 AI 생성 대본 저장
      const savedScript = addAIGeneratedScript(data);
      
      // 결과 영역으로 스크롤
      setTimeout(() => {
        const resultElement = document.getElementById('result');
        if (resultElement) {
          resultElement.scrollIntoView({ behavior: 'smooth' });
        }
      }, 100);

    } catch (error) {
      console.error('대본 생성 오류:', error);
      setError(error.message || '대본 생성 중 오류가 발생했습니다.');
    } finally {
      setIsGenerating(false);
    }
  };

  const genres = [
    '로맨스', '코미디', '스릴러', '드라마', '액션', 
    '공포', '판타지', 'SF', '미스터리', '시대극'
  ];

  const emotions = [
    '기쁨', '슬픔', '분노', '사랑', '그리움', 
    '희망', '절망', '놀라움', '두려움', '평온'
  ];

  const lengths = [
    { value: 'short', label: '짧게 (1-2분)' },
    { value: 'medium', label: '중간 (3-5분)' },
    { value: 'long', label: '길게 (5-10분)' }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 via-pink-50 to-blue-50 py-12">
      <div className="container mx-auto px-4">
        <div className="max-w-4xl mx-auto">
          
          {/* 페이지 헤더 */}
          <div className="text-center mb-12">
            <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full mb-6">
              <Sparkles className="w-10 h-10 text-white" />
            </div>
            <h1 className="text-4xl md:text-5xl font-bold text-gray-900 mb-4">
              AI 대본 생성기
            </h1>
            <p className="text-xl text-gray-600 max-w-2xl mx-auto leading-relaxed">
              인공지능의 힘으로 당신만의 특별한 연기 대본을 만들어보세요. 
              몇 가지 설정만으로 완전히 새로운 대본이 탄생합니다.
            </p>
          </div>

          {/* 메인 폼 */}
          <div className="bg-white rounded-2xl shadow-2xl p-8 md:p-12">
            <form onSubmit={handleGenerate} className="space-y-8">
              
              {/* 등장인물 수 */}
              <div className="space-y-3">
                <label className="flex items-center text-lg font-semibold text-gray-800 mb-3">
                  <Users className="w-6 h-6 mr-3 text-purple-500" />
                  등장인물 수
                </label>
                <select
                  name="characterCount"
                  value={formData.characterCount}
                  onChange={handleInputChange}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-colors text-lg"
                >
                  <option value="">선택해주세요</option>
                  <option value="1">1명 (독백)</option>
                  <option value="2">2명 (대화)</option>
                  <option value="3">3명 (그룹)</option>
                  <option value="4">4명 (앙상블)</option>
                  <option value="5+">5명 이상</option>
                </select>
              </div>

              {/* 장르 선택 */}
              <div className="space-y-3">
                <label className="flex items-center text-lg font-semibold text-gray-800 mb-3">
                  <Palette className="w-6 h-6 mr-3 text-purple-500" />
                  장르
                </label>
                <div className="grid grid-cols-2 md:grid-cols-5 gap-3">
                  {genres.map((genre) => (
                    <label key={genre} className="relative">
                      <input
                        type="radio"
                        name="genre"
                        value={genre}
                        onChange={handleInputChange}
                        className="sr-only peer"
                      />
                      <div className="px-4 py-3 bg-gray-50 border border-gray-200 rounded-lg text-center cursor-pointer transition-all hover:bg-gray-100 peer-checked:bg-purple-500 peer-checked:text-white peer-checked:border-purple-500 peer-checked:shadow-md">
                        {genre}
                      </div>
                    </label>
                  ))}
                </div>
              </div>

              {/* 감정 선택 */}
              <div className="space-y-3">
                <label className="flex items-center text-lg font-semibold text-gray-800 mb-3">
                  <Heart className="w-6 h-6 mr-3 text-purple-500" />
                  주요 감정
                </label>
                <div className="grid grid-cols-2 md:grid-cols-5 gap-3">
                  {emotions.map((emotion) => (
                    <label key={emotion} className="relative">
                      <input
                        type="radio"
                        name="emotion"
                        value={emotion}
                        onChange={handleInputChange}
                        className="sr-only peer"
                      />
                      <div className="px-4 py-3 bg-gray-50 border border-gray-200 rounded-lg text-center cursor-pointer transition-all hover:bg-gray-100 peer-checked:bg-pink-500 peer-checked:text-white peer-checked:border-pink-500 peer-checked:shadow-md">
                        {emotion}
                      </div>
                    </label>
                  ))}
                </div>
              </div>

              {/* 대본 길이 */}
              <div className="space-y-3">
                <label className="flex items-center text-lg font-semibold text-gray-800 mb-3">
                  <Clock className="w-6 h-6 mr-3 text-purple-500" />
                  대본 길이
                </label>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  {lengths.map((length) => (
                    <label key={length.value} className="relative">
                      <input
                        type="radio"
                        name="length"
                        value={length.value}
                        onChange={handleInputChange}
                        className="sr-only peer"
                      />
                      <div className="px-6 py-4 bg-gray-50 border border-gray-200 rounded-lg text-center cursor-pointer transition-all hover:bg-gray-100 peer-checked:bg-gradient-to-r peer-checked:from-purple-500 peer-checked:to-pink-500 peer-checked:text-white peer-checked:border-purple-500 peer-checked:shadow-lg">
                        <div className="font-medium">{length.label}</div>
                      </div>
                    </label>
                  ))}
                </div>
              </div>

              {/* 생성 버튼 */}
              <div className="pt-6">
                <button
                  type="submit"
                  disabled={isGenerating}
                  className={`w-full py-4 px-8 text-xl font-semibold rounded-xl transition-all duration-300 transform ${
                    isGenerating
                      ? 'bg-gray-400 cursor-not-allowed'
                      : 'bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 hover:scale-105 shadow-lg hover:shadow-xl'
                  } text-white`}
                >
                  {isGenerating ? (
                    <div className="flex items-center justify-center space-x-3">
                      <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-white"></div>
                      <span>AI가 대본을 생성하고 있습니다...</span>
                    </div>
                  ) : (
                    <div className="flex items-center justify-center space-x-3">
                      <Wand2 className="w-6 h-6" />
                      <span>대본 생성하기</span>
                    </div>
                  )}
                </button>
              </div>
            </form>

            {/* 안내 문구 */}
            <div className="mt-8 p-6 bg-gradient-to-r from-purple-50 to-pink-50 rounded-xl border border-purple-100">
              <div className="flex items-start space-x-3">
                <Sparkles className="w-6 h-6 text-purple-500 mt-1 flex-shrink-0" />
                <div>
                  <h3 className="font-semibold text-gray-800 mb-2">AI 대본 생성 안내</h3>
                  <ul className="text-sm text-gray-600 space-y-1">
                    <li>• 선택하신 조건에 맞는 창의적인 대본을 AI가 생성합니다</li>
                    <li>• 생성된 대본은 자동으로 저장되어 마이페이지에서 확인할 수 있습니다</li>
                    <li>• 마음에 들지 않으면 언제든 다시 생성할 수 있습니다</li>
                    <li>• 생성된 대본은 자유롭게 수정하여 사용하실 수 있습니다</li>
                  </ul>
                </div>
              </div>
            </div>
          </div>

          {/* 에러 메시지 */}
          {error && (
            <div className="mt-8 bg-red-50 border border-red-200 rounded-lg p-6">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                  </svg>
                </div>
                <div className="ml-3">
                  <h3 className="text-sm font-medium text-red-800">오류가 발생했습니다</h3>
                  <div className="mt-2 text-sm text-red-700">
                    <p>{error}</p>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* 생성된 대본 결과 */}
          {generatedScript && (
            <div id="result" className="mt-8 bg-white rounded-2xl shadow-2xl p-8 md:p-12">
              <div className="text-center mb-8">
                <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-green-500 to-emerald-500 rounded-full mb-4">
                  <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                  </svg>
                </div>
                <h2 className="text-3xl font-bold text-gray-900 mb-2">AI 대본 생성 완료!</h2>
                <p className="text-gray-600">AI가 생성한 대본을 확인하고 연습에 활용해보세요.</p>
              </div>

              <div className="bg-gray-50 rounded-xl p-6 border-2 border-gray-200">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg font-semibold text-gray-800">생성된 대본</h3>
                  <div className="flex space-x-4 text-sm text-gray-600">
                    <span>등장인물: {formData.characterCount}명</span>
                    <span>장르: {formData.genre}</span>
                    <span>감정: {formData.emotion}</span>
                  </div>
                </div>
                <div 
                  className="bg-white rounded-lg p-6 border border-gray-200 text-gray-800 leading-relaxed whitespace-pre-wrap font-mono text-sm"
                  style={{ whiteSpace: 'pre-wrap' }}
                >
                  {generatedScript}
                </div>
              </div>

              <div className="mt-6 flex flex-col sm:flex-row gap-4">
                <button
                  onClick={() => {
                    setGeneratedScript('');
                    setError('');
                    setFormData({
                      characterCount: '',
                      genre: '',
                      emotion: '',
                      length: ''
                    });
                    window.scrollTo({ top: 0, behavior: 'smooth' });
                  }}
                  className="flex-1 px-6 py-3 bg-gray-500 hover:bg-gray-600 text-white rounded-lg font-medium transition-colors"
                >
                  새로운 대본 생성하기
                </button>
                <button
                  onClick={() => {
                    navigator.clipboard.writeText(generatedScript);
                    alert('대본이 클립보드에 복사되었습니다!');
                  }}
                  className="flex-1 px-6 py-3 bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 text-white rounded-lg font-medium transition-colors"
                >
                  대본 복사하기
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AIScript;
