import React, { useState, useEffect, useCallback } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { ArrowLeft, Users, Eye, Calendar, Heart } from 'lucide-react';
import { scriptAPI } from '../services/api';
import { useAuth } from '../contexts/AuthContext';

const ScriptDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { isAuthenticated, addLikedScript, removeLikedScript, isScriptLiked } = useAuth();
  const [script, setScript] = useState(null);
  const [relatedScripts, setRelatedScripts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [likeCount, setLikeCount] = useState(0);
  const [isLiked, setIsLiked] = useState(false);

  const fetchScript = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      
      // 백엔드가 없으므로 더미 대본 데이터 사용
      const dummyScripts = {
        '1': {
          _id: '1',
          title: '첫사랑의 추억',
          content: `**민준**: (교실 문 앞에서 망설이다가) 서연아...

**서연**: (책을 읽다가 고개를 든다) 어? 민준아! 무슨 일이야?

**민준**: (얼굴이 빨개지며) 그... 그게... 너한테 하고 싶은 말이 있어서...

**서연**: (미소를 지으며) 앉아. 뭐야?

**민준**: (가방끈을 만지작거리며) 서연아, 나... 너를... 

**서연**: (조용히) 응?

**민준**: 너를 좋아해. 정말 많이 좋아해. 처음 만난 그날부터 계속...

**서연**: (잠시 놀란 표정을 짓다가 따뜻하게 미소) 나도... 나도 너를 좋아해, 민준아.

**민준**: (믿을 수 없다는 표정으로) 정말?

**서연**: (끄덕이며) 정말이야. 너무 늦게 말한 거 아니야?

**민준**: (환하게 웃으며) 미안해... 용기가 없었어.

**서연**: (손을 내밀며) 이제 괜찮아. 우리 함께 가자.`,
          situation: '고등학교 교실에서 첫사랑을 고백하는 상황',
          characterCount: 2,
          views: 1250,
          emotions: ['그리움', '사랑', '설렘'],
          createdAt: '2024-01-15T00:00:00.000Z',
          author: {
            name: '김작가',
            username: 'writer_kim'
          }
        },
        '2': {
          _id: '2',
          title: '이별의 순간',
          content: `**지수**: (커피잔을 돌리며) 우리... 이제 그만하자.

**현우**: (놀란 듯) 갑자기 왜? 무슨 일이야?

**지수**: (고개를 떨구며) 갑자기가 아니야. 계속 생각해왔어.

**현우**: (절망적으로) 내가 뭘 잘못했어? 말해줘.

**지수**: (눈물을 참으며) 잘못한 건 없어. 그냥... 우리가 너무 달라.

**현우**: 다르다고 해서 헤어져야 해? 맞춰갈 수 있잖아.

**지수**: (고개를 젓는다) 맞춰가는 게 아니라 억지로 맞추는 거야. 너도 힘들잖아.

**현우**: (목소리가 떨리며) 힘들어도 괜찮아. 너랑 함께라면...

**지수**: (일어서며) 미안해, 현우야. 정말 미안해.

**현우**: (지수의 손을 잡으며) 지수야, 제발...

**지수**: (손을 뿌리치며) 안녕, 현우야. 행복해.`,
          situation: '카페에서 연인이 이별을 통보하는 상황',
          characterCount: 2,
          views: 890,
          emotions: ['슬픔', '그리움', '절망'],
          createdAt: '2024-01-10T00:00:00.000Z',
          author: {
            name: '박작가',
            username: 'writer_park'
          }
        },
        '3': {
          _id: '3',
          title: '희망의 메시지',
          content: `**유진**: (거울을 보며 자신에게) 괜찮아, 유진아. 넌 할 수 있어.

**유진**: (심호흡을 하며) 오늘은 새로운 시작이야. 지난 일은 지난 일이고...

**유진**: (핸드폰을 보며) 면접이 두 시간 후네. 완벽하게 준비했어.

**유진**: (옷을 다시 정리하며) 이 회사에서 꼭 일하고 싶어. 내 꿈을 이룰 수 있을 거야.

**유진**: (가방을 챙기며) 엄마, 아빠... 제가 해낼게요. 꼭 해낼 거예요.

**유진**: (문을 나서며) 자, 이제 출발하자. 새로운 인생을 향해!

**유진**: (밝게 웃으며) 오늘부터 달라진 내가 될 거야. 분명히!`,
          situation: '취업 면접을 앞두고 자신을 격려하는 독백',
          characterCount: 1,
          views: 750,
          emotions: ['희망', '기쁨', '다짐'],
          createdAt: '2024-01-05T00:00:00.000Z',
          author: {
            name: '이작가',
            username: 'writer_lee'
          }
        }
      };

      const currentScript = dummyScripts[id];
      if (!currentScript) {
        setError('해당 대본을 찾을 수 없습니다.');
        return;
      }

      setScript(currentScript);
      
      // 관련 대본 (다른 대본들)
      const related = Object.values(dummyScripts)
        .filter(s => s._id !== id)
        .slice(0, 3);
      setRelatedScripts(related);

    } catch (error) {
      console.error('대본 조회 실패:', error);
      setError('대본을 불러오는 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  }, [id]);

  const incrementViewCount = useCallback(async () => {
    try {
      // 조회수 증가 API 호출 (백그라운드에서 실행)
      await scriptAPI.incrementView(id);
      console.log(`대본 ${id} 조회수 증가 완료`);
    } catch (error) {
      // 조회수 증가 실패는 사용자에게 보이지 않음
      console.warn('조회수 업데이트 실패:', error);
    }
  }, [id]);

  useEffect(() => {
    if (!id) return;

    // 즉시 실행 함수로 비동기 작업 처리
    (async () => {
      // 1. 대본 데이터 로드
      await fetchScript();
      
      // 2. 조회수 증가 (세션당 한 번만)
      const viewKey = `script_viewed_${id}`;
      const hasViewed = sessionStorage.getItem(viewKey);
      
      if (!hasViewed) {
        await incrementViewCount();
        sessionStorage.setItem(viewKey, 'true');
      }
    })();
  }, [id, fetchScript, incrementViewCount]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">로딩 중...</p>
        </div>
      </div>
    );
  }

  if (error || !script) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <p className="text-red-600 text-lg mb-4">{error || '대본을 찾을 수 없습니다.'}</p>
          <button
            onClick={() => navigate('/scripts')}
            className="btn-primary"
          >
            목록으로 돌아가기
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto px-4 py-8">
        {/* 뒤로가기 버튼 */}
        <div className="mb-6">
          <Link
            to="/scripts"
            className="inline-flex items-center text-gray-600 hover:text-gray-800 transition duration-200"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            목록으로 돌아가기
          </Link>
        </div>

        {/* 대본 메타 정보 */}
        <div className="bg-white rounded-lg shadow-md p-8 mb-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-6">{script.title}</h1>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
            <div>
              <div className="flex items-center text-gray-600 mb-3">
                <Users className="w-5 h-5 mr-2" />
                <span>등장인물: <strong>{script.characterCount}명</strong></span>
              </div>
              
              <div className="flex items-center text-gray-600 mb-3">
                <Eye className="w-5 h-5 mr-2" />
                <span>조회수: <strong>{script.views}</strong></span>
              </div>
              
              <div className="flex items-center text-gray-600">
                <Calendar className="w-5 h-5 mr-2" />
                <span>등록일: <strong>{new Date(script.createdAt).toLocaleDateString('ko-KR')}</strong></span>
              </div>
            </div>
            
            <div>
              <h3 className="text-lg font-semibold text-gray-800 mb-3">감정 태그</h3>
              <div className="flex flex-wrap">
                {script.emotions?.map((emotion, index) => (
                  <Link
                    key={index}
                    to={`/scripts?emotion=${encodeURIComponent(emotion)}`}
                    className="emotion-tag hover:bg-primary-100 hover:text-primary-700 transition duration-200"
                  >
                    #{emotion}
                  </Link>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* 상황 설명 */}
        <div className="bg-white rounded-lg shadow-md p-8 mb-8">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">상황 설명</h2>
          <p className="text-gray-700 leading-relaxed text-lg">
            {script.situation}
          </p>
        </div>

        {/* 대본 내용 */}
        <div className="bg-white rounded-lg shadow-md p-8 mb-8">
          <h2 className="text-2xl font-bold text-gray-800 mb-6">대본 내용</h2>
          <div className="bg-gray-50 rounded-lg p-6">
            <pre className="whitespace-pre-wrap font-sans text-gray-800 leading-relaxed text-lg">
              {script.content}
            </pre>
          </div>
        </div>

        {/* 관련 대본 */}
        {relatedScripts.length > 0 && (
          <div className="mb-8">
            <div className="flex items-center mb-6">
              <Heart className="w-6 h-6 text-red-500 mr-3" />
              <h2 className="text-2xl font-bold text-gray-800">관련 대본</h2>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {relatedScripts.map((relatedScript) => (
                <RelatedScriptCard key={relatedScript._id} script={relatedScript} />
              ))}
            </div>
          </div>
        )}

        {/* 액션 버튼 */}
        <div className="flex justify-center space-x-4">
          <Link
            to="/scripts"
            className="btn-secondary"
          >
            다른 대본 보기
          </Link>
          
          <Link
            to="/add-script"
            className="btn-primary"
          >
            새 대본 등록
          </Link>
        </div>
      </div>
    </div>
  );
};

// 관련 대본 카드 컴포넌트
const RelatedScriptCard = ({ script }) => {
  return (
    <div className="card p-6">
      <h3 className="text-lg font-semibold mb-3 text-gray-800">
        {script.title}
      </h3>
      
      <div className="flex items-center text-sm text-gray-600 mb-3">
        <Users className="w-4 h-4 mr-1" />
        <span>{script.characterCount}명</span>
        
        <Eye className="w-4 h-4 ml-4 mr-1" />
        <span>{script.views}</span>
      </div>
      
      <div className="mb-4">
        {script.emotions?.slice(0, 3).map((emotion, index) => (
          <span key={index} className="emotion-tag">
            #{emotion}
          </span>
        ))}
        {script.emotions?.length > 3 && (
          <span className="emotion-tag">+{script.emotions.length - 3}</span>
        )}
      </div>
      
      <Link
        to={`/scripts/${script._id}`}
        className="btn-primary inline-block text-center w-full text-sm"
      >
        자세히 보기
      </Link>
    </div>
  );
};

export default ScriptDetail;
