import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { ArrowRight, TrendingUp, Clock, Search, Filter, Heart, Users, Eye } from 'lucide-react';
import { scriptAPI } from '../services/api';

const Home = () => {
  const [popularScripts, setPopularScripts] = useState([]);
  const [latestScripts, setLatestScripts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchHomeData();
  }, []);

  const fetchHomeData = async () => {
    try {
      setLoading(true);
      const [popularResponse, latestResponse] = await Promise.all([
        scriptAPI.getPopular(),
        scriptAPI.getLatest()
      ]);
      
      setPopularScripts(popularResponse.data || []);
      setLatestScripts(latestResponse.data || []);
    } catch (error) {
      console.error('홈 데이터 로딩 실패:', error);
    } finally {
      setLoading(false);
    }
  };

  const ScriptCard = ({ script, index }) => (
    <Link 
      to={`/scripts/${script._id}`} 
      className={`glass hover-glow rounded-xl p-4 transition-all duration-300 hover:scale-105 border border-white/20 ${
        index % 2 === 0 ? 'float' : 'float-delayed'
      }`}
    >
      <div className="space-y-3">
        <h3 className="font-semibold text-white text-lg line-clamp-2 text-glow">
          {script.title}
        </h3>
        
        <div className="flex flex-wrap gap-1">
          {script.emotions?.slice(0, 2).map((emotion, idx) => (
            <span 
              key={idx} 
              className="px-2 py-1 text-xs glass-light rounded-full text-white/80 border border-white/20"
            >
              {emotion}
            </span>
          ))}
          {script.emotions?.length > 2 && (
            <span className="px-2 py-1 text-xs glass-light rounded-full text-white/60 border border-white/20">
              +{script.emotions.length - 2}
            </span>
          )}
        </div>

        <div className="flex items-center justify-between text-sm text-white/70">
          <div className="flex items-center space-x-4">
            <span className="flex items-center">
              <Users className="w-4 h-4 mr-1" />
              {script.characterCount}명
            </span>
            <span className="flex items-center">
              <Eye className="w-4 h-4 mr-1" />
              {script.views || 0}
            </span>
          </div>
          <span className="text-white/50">
            {new Date(script.createdAt).toLocaleDateString()}
          </span>
        </div>
      </div>
    </Link>
  );

  const LoadingCard = ({ index }) => (
    <div className={`glass rounded-xl p-4 border border-white/20 ${
      index % 2 === 0 ? 'float' : 'float-delayed'
    }`}>
      <div className="space-y-3">
        <div className="h-6 glass-light rounded animate-pulse"></div>
        <div className="flex gap-2">
          <div className="h-6 w-16 glass-light rounded-full animate-pulse"></div>
          <div className="h-6 w-12 glass-light rounded-full animate-pulse"></div>
        </div>
        <div className="flex justify-between">
          <div className="h-4 w-20 glass-light rounded animate-pulse"></div>
          <div className="h-4 w-16 glass-light rounded animate-pulse"></div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="min-h-screen">
      {/* 히어로 섹션 */}
      <section className="relative py-20 px-4 text-center">
        <div className="max-w-4xl mx-auto">
          <h1 className="text-5xl md:text-6xl font-bold text-white mb-6 text-glow">
            ✨ 연기 대본 라이브러리
          </h1>
          <p className="text-xl md:text-2xl text-white/80 mb-8 max-w-2xl mx-auto">
            다양한 감정과 상황의 연기 대본을 찾아보세요
          </p>
          
          <div className="flex flex-col sm:flex-row gap-4 justify-center items-center">
            <Link 
              to="/scripts" 
              className="glass-button text-white px-8 py-4 rounded-xl font-semibold text-lg flex items-center hover:scale-105 transition-all duration-300"
            >
              <Search className="w-5 h-5 mr-2" />
              대본 찾아보기
              <ArrowRight className="w-5 h-5 ml-2" />
            </Link>
            
            <Link 
              to="/add-script" 
              className="glass text-white/90 hover:text-white px-8 py-4 rounded-xl font-semibold text-lg flex items-center hover:scale-105 transition-all duration-300 border border-white/30"
            >
              <Heart className="w-5 h-5 mr-2" />
              대본 등록하기
            </Link>
          </div>
        </div>
      </section>

      {/* 인기 & 최신 대본 섹션 */}
      <section className="py-16 px-4">
        <div className="max-w-7xl mx-auto">
          <div className="grid md:grid-cols-2 gap-12">
            {/* 인기 대본 */}
            <div className="space-y-6">
              <div className="flex items-center space-x-3">
                <TrendingUp className="w-6 h-6 text-white" />
                <h2 className="text-2xl font-bold text-white text-glow">인기 대본</h2>
              </div>
              
              <div className="glass-strong rounded-2xl p-6 border border-white/20">
                <div className="space-y-4">
                  {loading ? (
                    Array.from({ length: 4 }).map((_, index) => (
                      <LoadingCard key={index} index={index} />
                    ))
                  ) : popularScripts.length > 0 ? (
                    popularScripts.slice(0, 4).map((script, index) => (
                      <ScriptCard key={script._id} script={script} index={index} />
                    ))
                  ) : (
                    <div className="text-center py-8">
                      <p className="text-white/60">아직 등록된 대본이 없습니다.</p>
                    </div>
                  )}
                </div>
                
                <div className="mt-6 text-center">
                  <Link 
                    to="/scripts?sort=popular" 
                    className="inline-flex items-center text-white/80 hover:text-white transition-colors duration-200 hover:scale-105"
                  >
                    더 보기
                    <ArrowRight className="w-4 h-4 ml-1" />
                  </Link>
                </div>
              </div>
            </div>

            {/* 최신 대본 */}
            <div className="space-y-6">
              <div className="flex items-center space-x-3">
                <Clock className="w-6 h-6 text-white" />
                <h2 className="text-2xl font-bold text-white text-glow">최신 대본</h2>
              </div>
              
              <div className="glass-strong rounded-2xl p-6 border border-white/20">
                <div className="space-y-4">
                  {loading ? (
                    Array.from({ length: 4 }).map((_, index) => (
                      <LoadingCard key={index} index={index} />
                    ))
                  ) : latestScripts.length > 0 ? (
                    latestScripts.slice(0, 4).map((script, index) => (
                      <ScriptCard key={script._id} script={script} index={index} />
                    ))
                  ) : (
                    <div className="text-center py-8">
                      <p className="text-white/60">아직 등록된 대본이 없습니다.</p>
                    </div>
                  )}
                </div>
                
                <div className="mt-6 text-center">
                  <Link 
                    to="/scripts" 
                    className="inline-flex items-center text-white/80 hover:text-white transition-colors duration-200 hover:scale-105"
                  >
                    더 보기
                    <ArrowRight className="w-4 h-4 ml-1" />
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* 기능 소개 섹션 */}
      <section className="py-16 px-4">
        <div className="max-w-6xl mx-auto">
          <h2 className="text-3xl font-bold text-center text-white mb-12 text-glow">
            왜 연기대본을 선택해야 할까요?
          </h2>
          
          <div className="grid md:grid-cols-3 gap-8">
            <div className="glass hover-glow rounded-xl p-6 text-center transition-all duration-300 hover:scale-105 border border-white/20">
              <div className="w-16 h-16 glass-button rounded-full flex items-center justify-center mx-auto mb-4">
                <Search className="w-8 h-8 text-white" />
              </div>
              <h3 className="text-xl font-semibold text-white mb-3 text-glow">쉬운 검색</h3>
              <p className="text-white/70">
                감정, 인원수, 장르별로 원하는 대본을 빠르게 찾을 수 있습니다.
              </p>
            </div>

            <div className="glass hover-glow rounded-xl p-6 text-center transition-all duration-300 hover:scale-105 border border-white/20">
              <div className="w-16 h-16 glass-button rounded-full flex items-center justify-center mx-auto mb-4">
                <Heart className="w-8 h-8 text-white" />
              </div>
              <h3 className="text-xl font-semibold text-white mb-3 text-glow">다양한 대본</h3>
              <p className="text-white/70">
                코미디부터 드라마까지 다양한 장르의 대본이 준비되어 있습니다.
              </p>
            </div>

            <div className="glass hover-glow rounded-xl p-6 text-center transition-all duration-300 hover:scale-105 border border-white/20">
              <div className="w-16 h-16 glass-button rounded-full flex items-center justify-center mx-auto mb-4">
                <Users className="w-8 h-8 text-white" />
              </div>
              <h3 className="text-xl font-semibold text-white mb-3 text-glow">커뮤니티</h3>
              <p className="text-white/70">
                연기자들이 직접 만든 대본을 공유하고 활용할 수 있습니다.
              </p>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
