import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Search, Menu, X, User, LogOut, LogIn, UserPlus } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);
  const navigate = useNavigate();
  const { user, isAuthenticated, logout, loading } = useAuth();

  const handleLogout = async () => {
    await logout();
    setIsUserMenuOpen(false);
    navigate('/');
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleUserMenu = () => {
    setIsUserMenuOpen(!isUserMenuOpen);
  };

  const closeMenus = () => {
    setIsMenuOpen(false);
    setIsUserMenuOpen(false);
  };

  return (
    <nav className="glass sticky top-0 z-50 border-0 border-b border-white/20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          {/* 로고 */}
          <div className="flex items-center">
            <Link 
              to="/" 
              className="flex-shrink-0 flex items-center group"
              onClick={closeMenus}
            >
              <span className="text-2xl font-bold text-white text-glow group-hover:scale-105 transition-transform duration-300">
                ✨ 연기대본
              </span>
            </Link>
          </div>

          {/* 데스크톱 메뉴 */}
          <div className="hidden md:flex items-center space-x-8">
            <Link 
              to="/" 
              className="text-white/90 hover:text-white px-3 py-2 rounded-lg text-sm font-medium transition-all duration-300 hover:bg-white/10 hover-glow"
            >
              홈
            </Link>
            <Link 
              to="/scripts" 
              className="text-white/90 hover:text-white px-3 py-2 rounded-lg text-sm font-medium transition-all duration-300 hover:bg-white/10 hover-glow"
            >
              대본 목록
            </Link>
            <Link 
              to="/add-script" 
              className="text-white/90 hover:text-white px-3 py-2 rounded-lg text-sm font-medium transition-all duration-300 hover:bg-white/10 hover-glow"
            >
              대본 등록
            </Link>

            {/* 인증 상태에 따른 메뉴 */}
            {loading ? (
              <div className="w-8 h-8 rounded-full glass animate-pulse"></div>
            ) : isAuthenticated ? (
              <div className="relative">
                <button
                  onClick={toggleUserMenu}
                  className="flex items-center space-x-2 text-white/90 hover:text-white px-3 py-2 rounded-lg text-sm font-medium transition-all duration-300 hover:bg-white/10 hover-glow"
                >
                  <User className="w-4 h-4" />
                  <span>{user?.name || user?.username}</span>
                </button>
                
                {/* 사용자 드롭다운 메뉴 */}
                {isUserMenuOpen && (
                  <div className="absolute right-0 mt-2 w-48 glass rounded-lg py-1 z-50 border border-white/20">
                    <div className="px-4 py-2 text-sm text-white/70 border-b border-white/10">
                      {user?.email}
                    </div>
                    <button
                      onClick={handleLogout}
                      className="w-full text-left flex items-center px-4 py-2 text-sm text-white/90 hover:text-white hover:bg-white/10 transition-colors duration-200"
                    >
                      <LogOut className="w-4 h-4 mr-2" />
                      로그아웃
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <div className="flex items-center space-x-4">
                <Link 
                  to="/login" 
                  className="flex items-center space-x-1 text-white/90 hover:text-white px-3 py-2 rounded-lg text-sm font-medium transition-all duration-300 hover:bg-white/10 hover-glow"
                >
                  <LogIn className="w-4 h-4" />
                  <span>로그인</span>
                </Link>
                <Link 
                  to="/register" 
                  className="flex items-center space-x-1 glass-button text-white px-4 py-2 rounded-lg text-sm font-medium"
                >
                  <UserPlus className="w-4 h-4" />
                  <span>회원가입</span>
                </Link>
              </div>
            )}
          </div>

          {/* 모바일 메뉴 버튼 */}
          <div className="md:hidden flex items-center">
            <button
              onClick={toggleMenu}
              className="inline-flex items-center justify-center p-2 rounded-lg text-white/90 hover:text-white hover:bg-white/10 transition-colors duration-200"
            >
              {isMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
            </button>
          </div>
        </div>

        {/* 모바일 메뉴 */}
        {isMenuOpen && (
          <div className="md:hidden">
            <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3 glass-light rounded-lg mt-2 border border-white/20">
              <Link 
                to="/" 
                className="block px-3 py-2 rounded-lg text-base font-medium text-white/90 hover:text-white hover:bg-white/10 transition-colors duration-200"
                onClick={closeMenus}
              >
                홈
              </Link>
              <Link 
                to="/scripts" 
                className="block px-3 py-2 rounded-lg text-base font-medium text-white/90 hover:text-white hover:bg-white/10 transition-colors duration-200"
                onClick={closeMenus}
              >
                대본 목록
              </Link>
              <Link 
                to="/add-script" 
                className="block px-3 py-2 rounded-lg text-base font-medium text-white/90 hover:text-white hover:bg-white/10 transition-colors duration-200"
                onClick={closeMenus}
              >
                대본 등록
              </Link>

              {/* 모바일 인증 메뉴 */}
              {loading ? (
                <div className="px-3 py-2">
                  <div className="h-4 glass rounded animate-pulse"></div>
                </div>
              ) : isAuthenticated ? (
                <div className="border-t border-white/10 pt-3">
                  <div className="px-3 py-2 text-sm text-white/70">
                    {user?.name || user?.username} ({user?.email})
                  </div>
                  <button
                    onClick={() => {
                      handleLogout();
                      closeMenus();
                    }}
                    className="w-full text-left flex items-center px-3 py-2 rounded-lg text-base font-medium text-white/90 hover:text-white hover:bg-white/10 transition-colors duration-200"
                  >
                    <LogOut className="w-4 h-4 mr-2" />
                    로그아웃
                  </button>
                </div>
              ) : (
                <div className="border-t border-white/10 pt-3 space-y-1">
                  <Link 
                    to="/login" 
                    className="flex items-center px-3 py-2 rounded-lg text-base font-medium text-white/90 hover:text-white hover:bg-white/10 transition-colors duration-200"
                    onClick={closeMenus}
                  >
                    <LogIn className="w-4 h-4 mr-2" />
                    로그인
                  </Link>
                  <Link 
                    to="/register" 
                    className="flex items-center px-3 py-2 rounded-lg text-base font-medium glass-button text-white"
                    onClick={closeMenus}
                  >
                    <UserPlus className="w-4 h-4 mr-2" />
                    회원가입
                  </Link>
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      {/* 배경 클릭 시 메뉴 닫기 */}
      {(isUserMenuOpen || isMenuOpen) && (
        <div 
          className="fixed inset-0 z-40 bg-black/20 backdrop-blur-sm" 
          onClick={closeMenus}
        ></div>
      )}
    </nav>
  );
};

export default Navbar;
