import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Users, Eye, Calendar, Heart, BookOpen, Filter, Grid3X3, Image, ArrowRight } from 'lucide-react';
import { scriptAPI, emotionAPI } from '../services/api';
import { dummyScripts, dummyEmotions } from '../data/dummyData';

const Home = () => {
  const [popularScripts, setPopularScripts] = useState([]);
  const [latestScripts, setLatestScripts] = useState([]);
  const [emotions, setEmotions] = useState([]);
  const [loading, setLoading] = useState(true);

  // ê°ì •ë³„ ì´ëª¨ì§€ ë§¤í•‘
  const getEmotionEmoji = (emotionName) => {
    const emojiMap = {
      'ê¸°ì¨': 'ğŸ˜Š',
      'í–‰ë³µ': 'ğŸ˜„',
      'ìŠ¬í””': 'ğŸ˜¢',
      'ìš°ìš¸': 'ğŸ˜”',
      'ë¶„ë…¸': 'ğŸ˜ ',
      'í™”ë‚¨': 'ğŸ˜¡',
      'ì‚¬ë‘': 'â¤ï¸',
      'ë¡œë§¨ìŠ¤': 'ğŸ’•',
      'ê·¸ë¦¬ì›€': 'ğŸ¥º',
      'ê·¸ë¦¬ì›Œ': 'ğŸ¤—',
      'ë¶ˆì•ˆ': 'ğŸ˜°',
      'ê±±ì •': 'ğŸ˜Ÿ',
      'ë‘ë ¤ì›€': 'ğŸ˜¨',
      'ë¬´ì„œì›€': 'ğŸ˜±',
      'ì ˆë§': 'ğŸ˜',
      'ì ˆëŒ€ì ˆëª…': 'ğŸ˜£',
      'ì¦ì˜¤': 'ğŸ˜¤',
      'í˜ì˜¤': 'ğŸ¤®',
      'ì§ˆíˆ¬': 'ğŸ˜’',
      'ì‹œê¸°': 'ğŸ™„',
      'ë†€ëŒ': 'ğŸ˜²',
      'ë†€ë¼ì›€': 'ğŸ˜¯',
      'ë‹¹í™©': 'ğŸ˜…',
      'ë¶€ë„ëŸ¬ì›€': 'ğŸ˜³',
      'ìˆ˜ì¤ìŒ': 'â˜ºï¸',
      'ìì‹ ê°': 'ğŸ˜',
      'ë‹¹ë‹¹í•¨': 'ğŸ’ª',
      'í¬ë§': 'ğŸŒŸ',
      'ê¿ˆ': 'âœ¨',
      'í‰í™”': 'ğŸ˜Œ',
      'í‰ì˜¨': 'ğŸ˜´',
      'ê³ ë¯¼': 'ğŸ¤”',
      'ìƒê°': 'ğŸ’­'
    };

    // ê°ì • ì´ë¦„ì—ì„œ í‚¤ì›Œë“œë¥¼ ì°¾ì•„ì„œ ë§¤í•‘
    for (const [key, emoji] of Object.entries(emojiMap)) {
      if (emotionName.includes(key)) {
        return emoji;
