import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Users, Eye, Calendar, Heart, BookOpen, Filter, Grid3X3, Image, ArrowRight } from 'lucide-react';
import { scriptAPI, emotionAPI } from '../services/api';
import { dummyScripts, dummyEmotions } from '../data/dummyData';

const Home = () => {
  const [popularScripts, setPopularScripts] = useState([]);
  const [latestScripts, setLatestScripts] = useState([]);
  const [emotions, setEmotions] = useState([]);
  const [loading, setLoading] = useState(true);

  // 감정별 이모지 매핑
  const getEmotionEmoji = (emotionName) => {
    const emojiMap = {
      '기쁨': '😊',
      '행복': '😄',
      '슬픔': '😢',
      '우울': '😔',
      '분노': '😠',
      '화남': '😡',
      '사랑': '❤️',
      '로맨스': '💕',
      '그리움': '🥺',
      '그리워': '🤗',
      '불안': '😰',
      '걱정': '😟',
      '두려움': '😨',
      '무서움': '😱',
      '절망': '😞',
      '절대절명': '😣',
      '증오': '😤',
      '혐오': '🤮',
      '질투': '😒',
      '시기': '🙄',
      '놀람': '😲',
      '놀라움': '😯',
      '당황': '😅',
      '부끄러움': '😳',
      '수줍음': '☺️',
      '자신감': '😎',
      '당당함': '💪',
      '희망': '🌟',
      '꿈': '✨',
      '평화': '😌',
      '평온': '😴',
      '고민': '🤔',
      '생각': '💭'
    };

    // 감정 이름에서 키워드를 찾아서 매핑
    for (const [key, emoji] of Object.entries(emojiMap)) {
      if (emotionName.includes(key)) {
        return emoji;
      }
    }

    return '🎭'; // 기본 이모지
  };

  useEffect(() => {
    const fetchData = async () => {
            try {
        // 실제 API 호출
        const [popularRes, latestRes, emotionsRes] = await Promise.all([
          scriptAPI.getPopular(),
          scriptAPI.getLatest(),
          emotionAPI.getAll()
        ]);

        setPopularScripts(popularRes.data || []);
        setLatestScripts(latestRes.data || []);
        setEmotions(emotionsRes.data || []);

      } catch (error) {
        console.error('데이터 로딩 실패:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="spinner mx-auto"></div>
          <p className="mt-4 text-secondary">로딩 중...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      {/* 히어로 섹션 */}
      <section className="hero py-16 lg:py-24">
        <div className="container">
          <div className="text-center max-w-4xl mx-auto">
            <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold mb-6">
              연기 대본 라이브러리
            </h1>
            <p className="text-xl md:text-2xl mb-4 opacity-90">
              연기 입시부터 오디션 준비까지, 필요한 모든 대본을 한 곳에
            </p>
            <p className="text-lg md:text-xl mb-10 opacity-80">
              감정별 · 길이별 · 상황별, 원하는 대본을 빠르게 찾으세요
            </p>

            {/* 메인 CTA 버튼 */}
            <div className="mb-8">
              <Link 
                to="/scripts" 
                className="inline-flex items-center px-8 py-4 bg-white text-emerald-600 text-lg font-semibold rounded-lg hover:bg-gray-50 hover:text-emerald-700 transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-1"
              >
                <BookOpen className="w-6 h-6 mr-3" />
                대본 라이브러리 둘러보기
              </Link>
            </div>

            {/* 추가 CTA 버튼들 */}
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link to="/add-script" className="btn bg-white/20 text-white border border-white/30 hover:bg-white/30">
                <Heart className="w-5 h-5 mr-2" />
                대본 등록하기
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* 메인 콘텐츠 */}
      <div className="container py-16">
        <div className="space-y-16">

          {/* 썸네일 대본 탐색 섹션 */}
          <ScriptThumbnailSection />

          {/* 인기 대본 & 최신 대본 섹션 */}
          <section>
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">

              {/* 인기 대본 */}
              <div className="space-y-6">
                <div className="flex-between">
                  <h2 className="section-title">인기 대본</h2>
                  <Link
                    to="/scripts?sort=popular"
                    className="text-primary hover:text-emerald-700 text-sm font-medium"
                  >
                    더 보기 →
                  </Link>
                </div>

                <div className="card">
                  <div className="space-y-4">
                    {popularScripts.slice(0, 4).map((script) => (
                      <ScriptListItem key={script._id} script={script} />
                    ))}
                  </div>
                </div>
              </div>

              {/* 최신 대본 */}
              <div className="space-y-6">
                <div className="flex-between">
                  <h2 className="section-title">최신 대본</h2>
                  <Link
                    to="/scripts?sort=latest"
                    className="text-primary hover:text-emerald-700 text-sm font-medium"
                  >
                    더 보기 →
                  </Link>
                </div>

                <div className="card">
                  <div className="space-y-4">
                    {latestScripts.slice(0, 4).map((script) => (
                      <ScriptListItem key={script._id} script={script} />
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </section>

          {/* 감정 카테고리 섹션 */}
          <section>
            <div className="text-center mb-12">
              <h2 className="section-title">감정별 대본 찾기</h2>
              <p className="text-secondary max-w-2xl mx-auto">
                원하는 감정이나 상황에 맞는 대본을 쉽게 찾아보세요
              </p>
            </div>

            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              {emotions.slice(0, 8).map((emotion) => (
                <Link
                  key={emotion._id}
                  to={`/scripts?emotion=${emotion._id}`}
                  className="card-hover text-center group"
                >
                  <div className="w-16 h-16 bg-gradient-to-br from-emerald-100 to-emerald-200 rounded-full flex items-center justify-center mx-auto mb-4 group-hover:scale-110 transition-transform">
                    <span className="text-3xl">{getEmotionEmoji(emotion.name)}</span>
                  </div>
                  <h3 className="font-semibold text-gray-900 mb-2">{emotion.name}</h3>
                  <p className="text-sm text-secondary">{emotion.description}</p>
                </Link>
              ))}
            </div>
          </section>

          {/* CTA 섹션 */}
          <section className="bg-gradient-to-r from-emerald-50 to-emerald-100 rounded-2xl p-8 lg:p-12 text-center">
            <h2 className="text-2xl lg:text-3xl font-bold text-gray-900 mb-4">
              나만의 대본을 공유해보세요
            </h2>
            <p className="text-lg text-secondary mb-8 max-w-2xl mx-auto">
              당신의 창작 대본을 다른 배우들과 공유하고, 
              피드백을 받아보세요
            </p>
            <Link to="/add-script" className="btn btn-primary">
              <Heart className="w-5 h-5 mr-2" />
              대본 등록하기
            </Link>
          </section>

        </div>
      </div>
    </div>
  );
};

// ScriptListItem 컴포넌트
const ScriptListItem = ({ script }) => {
  return (
    <Link
      to={`/scripts/${script._id}`}
      className="script-item group"
    >
      <div className="flex items-start justify-between">
        <div className="flex-1">
          <h3 className="font-semibold text-gray-900 group-hover:text-emerald-600 mb-2">
            {script.title}
          </h3>
          <p className="text-sm text-secondary mb-3 line-clamp-2">
            {script.situation}
          </p>
          <div className="flex items-center gap-4 text-xs text-gray-500">
            <span className="flex items-center">
              <Eye className="w-3 h-3 mr-1" />
              {script.views || 0}
            </span>
            <span className="flex items-center">
              <Calendar className="w-3 h-3 mr-1" />
              {new Date(script.createdAt).toLocaleDateString()}
            </span>
          </div>
        </div>
        {script.emotion && (
          <span className="emotion-tag ml-4">
            {script.emotion.name}
          </span>
        )}
      </div>
    </Link>
  );
};

// ScriptThumbnailSection 컴포넌트
const ScriptThumbnailSection = () => {
  const [thumbnailScripts, setThumbnailScripts] = useState([]);
  const [genderFilter, setGenderFilter] = useState('');
  const [countFilter, setCountFilter] = useState('');
  const [moodFilter, setMoodFilter] = useState('');
  const [sortBy, setSortBy] = useState('popular');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // 썸네일용 임시 이미지 배열
  const thumbnailImages = [
    'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=300&h=200&fit=crop&crop=faces',
    'https://images.unsplash.com/photo-1494790108755-2616b612b1e0?w=300&h=200&fit=crop&crop=faces',
    'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=300&h=200&fit=crop&crop=faces',
    'https://images.unsplash.com/photo-1517841905240-472988babdf9?w=300&h=200&fit=crop&crop=faces',
    'https://images.unsplash.com/photo-1500648767791-00dcc994a43e?w=300&h=200&fit=crop&crop=faces',
    'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=300&h=200&fit=crop&crop=faces',
    'https://images.unsplash.com/photo-1519345182560-3f2917c472ef?w=300&h=200&fit=crop&crop=faces',
    'https://images.unsplash.com/photo-1534528741775-53994a69daeb?w=300&h=200&fit=crop&crop=faces'
  ];

  useEffect(() => {
    const fetchThumbnailScripts = async () => {
      try {
        setLoading(true);

        // API 파라미터 구성
        const params = new URLSearchParams();
        if (genderFilter) params.append('gender', genderFilter);
        if (countFilter) {
          if (countFilter === '1인') params.append('characters', '1');
          else if (countFilter === '2인') params.append('characters', '2');
          else if (countFilter === '다인') params.append('characters', '3+');
        }
        if (moodFilter) params.append('mood', moodFilter);
        params.append('sort', sortBy);
        params.append('limit', '8'); // 썸네일용으로 8개만 가져오기

        const response = await scriptAPI.getAll(params);
        setThumbnailScripts(response.data.scripts || []);

      } catch (error) {
        console.error('썸네일 대본 조회 실패:', error);
        setThumbnailScripts([]);
      } finally {
        setLoading(false);
      }
    };

    fetchThumbnailScripts();
  }, [genderFilter, countFilter, moodFilter, sortBy]);

  // 좋아요 수 가져오기
  const getLikeCount = (scriptId) => {
    const likeCounts = JSON.parse(localStorage.getItem('scriptLikeCounts') || '{}');
    return likeCounts[scriptId] || 0;
  };

  // 카드 클릭 시 해당 카테고리로 이동
  const handleCardClick = (script) => {
    const params = new URLSearchParams();
    if (script.mood) params.append('mood', script.mood);
    if (script.gender && script.gender !== '전체') params.append('gender', script.gender);
    navigate(`/scripts?${params.toString()}`);
  };

  // 인원수 표시 텍스트
  const getCharacterText = (script) => {
    const gender = script.gender === '전체' ? '' : script.gender;
    const count = script.characterCount === 1 ? '독백' : `${script.characterCount}인극`;
    return gender ? `${gender} ${count}` : count;
  };

  return (
    <section className="mb-16">
      <div className="text-center mb-8">
        <div className="flex items-center justify-center mb-4">
          <Grid3X3 className="w-6 h-6 text-emerald-600 mr-2" />
          <h2 className="text-2xl lg:text-3xl font-bold text-gray-900">대본 썸네일 탐색</h2>
        </div>
        <p className="text-gray-600 max-w-2xl mx-auto">
          카테고리별로 대본을 썸네일로 탐색하고, 원하는 조건으로 필터링해보세요
        </p>
      </div>

      {/* 필터 섹션 */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 mb-8">
        <div className="flex items-center mb-4">
          <Filter className="w-5 h-5 text-gray-600 mr-2" />
          <h3 className="text-lg font-semibold text-gray-800">필터 & 정렬</h3>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
          {/* 성별 필터 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">성별</label>
            <select
              value={genderFilter}
              onChange={(e) => setGenderFilter(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            >
              <option value="">전체</option>
              <option value="남자">남자</option>
              <option value="여자">여자</option>
              <option value="혼성">혼성</option>
            </select>
          </div>

          {/* 인원수 필터 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">인원수</label>
            <select
              value={countFilter}
              onChange={(e) => setCountFilter(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            >
              <option value="">전체</option>
              <option value="1인">1인</option>
              <option value="2인">2인</option>
              <option value="다인">다인</option>
            </select>
          </div>

          {/* 분위기 필터 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">분위기</label>
            <select
              value={moodFilter}
              onChange={(e) => setMoodFilter(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            >
              <option value="">전체</option>
              <option value="감정적인">감정적인</option>
              <option value="코믹한">코믹한</option>
              <option value="진지한">진지한</option>
              <option value="로맨스">로맨스</option>
              <option value="스릴러">스릴러</option>
              <option value="판타지">판타지</option>
              <option value="SF">SF</option>
              <option value="시대극">시대극</option>
            </select>
          </div>

          {/* 정렬 */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">정렬</label>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            >
              <option value="popular">인기순</option>
              <option value="newest">최신순</option>
              <option value="title">제목순</option>
            </select>
          </div>
        </div>
      </div>

      {/* 썸네일 그리드 */}
      {loading ? (
        <div className="text-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">로딩 중...</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {thumbnailScripts.map((script, index) => (
            <div
              key={script._id}
              onClick={() => handleCardClick(script)}
              className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden cursor-pointer transition-all duration-200 hover:shadow-lg hover:-translate-y-1 group"
            >
              {/* 썸네일 이미지 */}
              <div className="relative h-40 bg-gradient-to-br from-emerald-100 to-emerald-200 overflow-hidden">
                <img
                  src={thumbnailImages[index % thumbnailImages.length]}
                  alt={script.title}
                  className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-200"
                  onError={(e) => {
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'flex';
                  }}
                />
                {/* 이미지 로드 실패 시 fallback */}
                <div className="absolute inset-0 bg-gradient-to-br from-emerald-100 to-emerald-200 hidden items-center justify-center">
                  <Image className="w-12 h-12 text-emerald-600" />
                </div>

                {/* 카테고리 배지 */}
                <div className="absolute top-3 left-3">
                  <span className="px-2 py-1 bg-white/90 text-emerald-600 text-xs font-semibold rounded-full">
                    {script.mood || '일반'}
                  </span>
                </div>
              </div>

              {/* 카드 내용 */}
              <div className="p-4">
                                 <h3 className="font-semibold text-gray-900 mb-2 overflow-hidden group-hover:text-emerald-600 transition-colors">
                   <span className="block truncate">{script.title}</span>
                 </h3>

                <p className="text-sm text-gray-600 mb-3">
                  {getCharacterText(script)}
                </p>

                {/* 통계 정보 */}
                <div className="flex items-center justify-between text-xs text-gray-500">
                  <div className="flex items-center space-x-3">
                    <span className="flex items-center">
                      <Eye className="w-3 h-3 mr-1" />
                      {script.views || 0}
                    </span>
                    <span className="flex items-center">
                      <Heart className="w-3 h-3 mr-1 text-red-500" />
                      {getLikeCount(script._id)}
                    </span>
                  </div>
                  <ArrowRight className="w-4 h-4 text-emerald-600 group-hover:translate-x-1 transition-transform" />
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* 더 보기 버튼 */}
      <div className="text-center mt-8">
        <Link
          to="/scripts"
          className="inline-flex items-center px-6 py-3 bg-emerald-600 text-white font-medium rounded-lg hover:bg-emerald-700 transition-colors"
        >
          <Grid3X3 className="w-5 h-5 mr-2" />
          모든 대본 보기
          <ArrowRight className="w-5 h-5 ml-2" />
        </Link>
      </div>
    </section>
  );
};

export default Home;
