import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Users, Eye, Calendar, Heart, BookOpen, User, Mic, GraduationCap, Star } from 'lucide-react';
import { scriptAPI, emotionAPI } from '../services/api';
import { dummyScripts, dummyEmotions } from '../data/dummyData';

const Home = () => {
  const [popularScripts, setPopularScripts] = useState([]);
  const [latestScripts, setLatestScripts] = useState([]);
  const [emotions, setEmotions] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Ïπ¥ÌÖåÍ≥†Î¶¨ Îç∞Ïù¥ÌÑ∞
  const categories = [
    {
      id: 'female-monologue',
      title: 'Ïó¨Ïûê ÎèÖÎ∞±',
      description: 'Ïó¨ÏÑ± Ï∫êÎ¶≠ÌÑ∞Ïùò Í∞êÏ†ïÏùÑ ÌëúÌòÑÌïòÎäî ÎèÖÎ∞± ÎåÄÎ≥∏',
      icon: User,
      query: 'gender=Ïó¨Ïûê&scriptType=ÎèÖÎ∞±',
      gradient: 'from-pink-400 to-rose-600',
      iconColor: 'text-pink-100'
    },
    {
      id: 'male-monologue', 
      title: 'ÎÇ®Ïûê ÎèÖÎ∞±',
      description: 'ÎÇ®ÏÑ± Ï∫êÎ¶≠ÌÑ∞Ïùò ÎÇ¥Î©¥ÏùÑ ÎìúÎü¨ÎÇ¥Îäî ÎèÖÎ∞± ÎåÄÎ≥∏',
      icon: Mic,
      query: 'gender=ÎÇ®Ïûê&scriptType=ÎèÖÎ∞±',
      gradient: 'from-blue-400 to-indigo-600',
      iconColor: 'text-blue-100'
    },
    {
      id: 'entrance-exam',
      title: 'ÏûÖÏãú ÎåÄÎ≥∏',
      description: 'Ïó∞Í∏∞ ÏûÖÏãúÏôÄ ÍµêÏú°Ïö© Ïó∞Ïäµ ÎåÄÎ≥∏',
      icon: GraduationCap,
      query: 'purpose=ÏàòÏóÖ/ÍµêÏú°',
      gradient: 'from-emerald-400 to-teal-600',
      iconColor: 'text-emerald-100'
    },
    {
      id: 'audition',
      title: 'Ïò§ÎîîÏÖò Ïö©',
      description: 'Ïò§ÎîîÏÖòÏóêÏÑú ÌôúÏö©Ìï† Ïàò ÏûàÎäî Ïã§Ï†Ñ ÎåÄÎ≥∏',
      icon: Star,
      query: 'purpose=Ïò§ÎîîÏÖò',
      gradient: 'from-amber-400 to-orange-600',
      iconColor: 'text-amber-100'
    }
  ];

  // Ïπ¥ÌÖåÍ≥†Î¶¨ ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨
  const handleCategoryClick = (query) => {
    navigate(`/scripts?${query}`);
  };

  // Í∞êÏ†ïÎ≥Ñ Ïù¥Î™®ÏßÄ Îß§Ìïë
  const getEmotionEmoji = (emotionName) => {
    const emojiMap = {
      'Í∏∞ÏÅ®': 'üòä',
      'ÌñâÎ≥µ': 'üòÑ',
      'Ïä¨Ìîî': 'üò¢',
      'Ïö∞Ïö∏': 'üòî',
      'Î∂ÑÎÖ∏': 'üò†',
      'ÌôîÎÇ®': 'üò°',
      'ÏÇ¨Îûë': '‚ù§Ô∏è',
      'Î°úÎß®Ïä§': 'üíï',
      'Í∑∏Î¶¨ÏõÄ': 'ü•∫',
      'Í∑∏Î¶¨Ïõå': 'ü§ó',
      'Î∂àÏïà': 'üò∞',
      'Í±±Ï†ï': 'üòü',
      'ÎëêÎ†§ÏõÄ': 'üò®',
      'Î¨¥ÏÑúÏõÄ': 'üò±',
      'Ï†àÎßù': 'üòû',
      'Ï†àÎåÄÏ†àÎ™Ö': 'üò£',
      'Ï¶ùÏò§': 'üò§',
      'ÌòêÏò§': 'ü§Æ',
      'ÏßàÌà¨': 'üòí',
      'ÏãúÍ∏∞': 'üôÑ',
      'ÎÜÄÎûå': 'üò≤',
      'ÎÜÄÎùºÏõÄ': 'üòØ',
      'ÎãπÌô©': 'üòÖ',
      'Î∂ÄÎÅÑÎü¨ÏõÄ': 'üò≥',
      'ÏàòÏ§çÏùå': '‚ò∫Ô∏è',
      'ÏûêÏã†Í∞ê': 'üòé',
      'ÎãπÎãπÌï®': 'üí™',
      'Ìù¨Îßù': 'üåü',
      'Íøà': '‚ú®',
      'ÌèâÌôî': 'üòå',
      'ÌèâÏò®': 'üò¥',
      'Í≥†ÎØº': 'ü§î',
      'ÏÉùÍ∞Å': 'üí≠'
    };

    // Í∞êÏ†ï Ïù¥Î¶ÑÏóêÏÑú ÌÇ§ÏõåÎìúÎ•º Ï∞æÏïÑÏÑú Îß§Ìïë
    for (const [key, emoji] of Object.entries(emojiMap)) {
      if (emotionName.includes(key)) {
        return emoji;
      }
    }

    return 'üé≠'; // Í∏∞Î≥∏ Ïù¥Î™®ÏßÄ
  };

  useEffect(() => {
    const fetchData = async () => {
            try {
        // Ïã§Ï†ú API Ìò∏Ï∂ú
        const [popularRes, latestRes, emotionsRes] = await Promise.all([
          scriptAPI.getPopular(),
          scriptAPI.getLatest(),
          emotionAPI.getAll()
        ]);

        setPopularScripts(popularRes.data || []);
        setLatestScripts(latestRes.data || []);
        setEmotions(emotionsRes.data || []);

      } catch (error) {
        console.error('Îç∞Ïù¥ÌÑ∞ Î°úÎî© Ïã§Ìå®:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="spinner mx-auto"></div>
          <p className="mt-4 text-secondary">Î°úÎî© Ï§ë...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      {/* ÌûàÏñ¥Î°ú ÏÑπÏÖò */}
      <section className="hero py-16 lg:py-24">
        <div className="container">
          <div className="text-center max-w-4xl mx-auto">
            <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold mb-6">
              Ïó∞Í∏∞ ÎåÄÎ≥∏ ÎùºÏù¥Î∏åÎü¨Î¶¨
            </h1>
            <p className="text-xl md:text-2xl mb-4 opacity-90">
              Ïó∞Í∏∞ ÏûÖÏãúÎ∂ÄÌÑ∞ Ïò§ÎîîÏÖò Ï§ÄÎπÑÍπåÏßÄ, ÌïÑÏöîÌïú Î™®Îì† ÎåÄÎ≥∏ÏùÑ Ìïú Í≥≥Ïóê
            </p>
            <p className="text-lg md:text-xl mb-10 opacity-80">
              Í∞êÏ†ïÎ≥Ñ ¬∑ Í∏∏Ïù¥Î≥Ñ ¬∑ ÏÉÅÌô©Î≥Ñ, ÏõêÌïòÎäî ÎåÄÎ≥∏ÏùÑ Îπ†Î•¥Í≤å Ï∞æÏúºÏÑ∏Ïöî
            </p>

            {/* Î©îÏù∏ CTA Î≤ÑÌäº */}
            <div className="mb-8">
              <Link 
                to="/scripts" 
                className="inline-flex items-center px-8 py-4 bg-white text-emerald-600 text-lg font-semibold rounded-lg hover:bg-gray-50 hover:text-emerald-700 transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-1"
              >
                <BookOpen className="w-6 h-6 mr-3" />
                ÎåÄÎ≥∏ ÎùºÏù¥Î∏åÎü¨Î¶¨ ÎëòÎü¨Î≥¥Í∏∞
              </Link>
            </div>

            {/* Ï∂îÍ∞Ä CTA Î≤ÑÌäºÎì§ */}
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link to="/add-script" className="btn bg-white/20 text-white border border-white/30 hover:bg-white/30">
                <Heart className="w-5 h-5 mr-2" />
                ÎåÄÎ≥∏ Îì±Î°ùÌïòÍ∏∞
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Î©îÏù∏ ÏΩòÌÖêÏ∏† */}
      <div className="container py-16">
        <div className="space-y-16">

          {/* Ïπ¥ÌÖåÍ≥†Î¶¨ ÏÑπÏÖò */}
          <section>
            <div className="text-center mb-12">
              <h2 className="text-3xl font-bold text-gray-800 mb-4">Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ ÎåÄÎ≥∏ Ï∞æÍ∏∞</h2>
              <p className="text-lg text-gray-600 max-w-2xl mx-auto">
                Î™©Ï†ÅÍ≥º Ïö©ÎèÑÏóê ÎßûÎäî ÎåÄÎ≥∏ÏùÑ Îπ†Î•¥Í≤å Ï∞æÏïÑÎ≥¥ÏÑ∏Ïöî
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-4xl mx-auto">
              {categories.map((category) => {
                const IconComponent = category.icon;
                return (
                  <div
                    key={category.id}
                    onClick={() => handleCategoryClick(category.query)}
                    className="group cursor-pointer"
                  >
                    <div className={`relative overflow-hidden rounded-xl bg-gradient-to-br ${category.gradient} p-8 text-white shadow-lg transition-all duration-300 hover:shadow-xl hover:scale-105`}>
                      {/* Î∞∞Í≤Ω Ìå®ÌÑ¥ */}
                      <div className="absolute inset-0 bg-black bg-opacity-10"></div>

                      {/* ÏïÑÏù¥ÏΩò */}
                      <div className="relative z-10 mb-4">
                        <div className={`inline-flex items-center justify-center w-16 h-16 rounded-full bg-white bg-opacity-20 ${category.iconColor}`}>
                          <IconComponent className="w-8 h-8" />
                        </div>
                      </div>

                      {/* Ïª®ÌÖêÏ∏† */}
                      <div className="relative z-10">
                        <h3 className="text-2xl font-bold mb-2 group-hover:text-yellow-100 transition-colors">
                          {category.title}
                        </h3>
                        <p className="text-white text-opacity-90 leading-relaxed">
                          {category.description}
                        </p>
                      </div>

                      {/* Ìò∏Î≤Ñ Ìö®Í≥º */}
                      <div className="absolute bottom-4 right-4 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                        <div className="w-8 h-8 bg-white bg-opacity-20 rounded-full flex items-center justify-center">
                          <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                          </svg>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </section>

          {/* Ïù∏Í∏∞ ÎåÄÎ≥∏ & ÏµúÏã† ÎåÄÎ≥∏ ÏÑπÏÖò */}
          <section>
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">

              {/* Ïù∏Í∏∞ ÎåÄÎ≥∏ */}
              <div className="space-y-6">
                <div className="flex-between">
                  <h2 className="section-title">Ïù∏Í∏∞ ÎåÄÎ≥∏</h2>
                  <Link
                    to="/scripts?sort=popular"
                    className="text-primary hover:text-emerald-700 text-sm font-medium"
                  >
                    Îçî Î≥¥Í∏∞ ‚Üí
                  </Link>
                </div>

                <div className="card">
                  <div className="space-y-4">
                    {popularScripts.slice(0, 4).map((script) => (
                      <ScriptListItem key={script._id} script={script} />
                    ))}
                  </div>
                </div>
              </div>

              {/* ÏµúÏã† ÎåÄÎ≥∏ */}
              <div className="space-y-6">
                <div className="flex-between">
                  <h2 className="section-title">ÏµúÏã† ÎåÄÎ≥∏</h2>
                  <Link
                    to="/scripts?sort=latest"
                    className="text-primary hover:text-emerald-700 text-sm font-medium"
                  >
                    Îçî Î≥¥Í∏∞ ‚Üí
                  </Link>
                </div>

                <div className="card">
                  <div className="space-y-4">
                    {latestScripts.slice(0, 4).map((script) => (
                      <ScriptListItem key={script._id} script={script} />
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </section>

          {/* Í∞êÏ†ï Ïπ¥ÌÖåÍ≥†Î¶¨ ÏÑπÏÖò */}
          <section>
            <div className="text-center mb-12">
              <h2 className="section-title">Í∞êÏ†ïÎ≥Ñ ÎåÄÎ≥∏ Ï∞æÍ∏∞</h2>
              <p className="text-secondary max-w-2xl mx-auto">
                ÏõêÌïòÎäî Í∞êÏ†ïÏù¥ÎÇò ÏÉÅÌô©Ïóê ÎßûÎäî ÎåÄÎ≥∏ÏùÑ ÏâΩÍ≤å Ï∞æÏïÑÎ≥¥ÏÑ∏Ïöî
              </p>
            </div>

            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
              {emotions.slice(0, 8).map((emotion) => (
                <Link
                  key={emotion._id}
                  to={`/scripts?emotion=${emotion._id}`}
                  className="card-hover text-center group"
                >
                  <div className="w-16 h-16 bg-gradient-to-br from-emerald-100 to-emerald-200 rounded-full flex items-center justify-center mx-auto mb-4 group-hover:scale-110 transition-transform">
                    <span className="text-3xl">{getEmotionEmoji(emotion.name)}</span>
                  </div>
                  <h3 className="font-semibold text-gray-900 mb-2">{emotion.name}</h3>
                  <p className="text-sm text-secondary">{emotion.description}</p>
                </Link>
              ))}
            </div>
          </section>

          {/* CTA ÏÑπÏÖò */}
          <section className="bg-gradient-to-r from-emerald-50 to-emerald-100 rounded-2xl p-8 lg:p-12 text-center">
            <h2 className="text-2xl lg:text-3xl font-bold text-gray-900 mb-4">
              ÎÇòÎßåÏùò ÎåÄÎ≥∏ÏùÑ Í≥µÏú†Ìï¥Î≥¥ÏÑ∏Ïöî
            </h2>
            <p className="text-lg text-secondary mb-8 max-w-2xl mx-auto">
              ÎãπÏã†Ïùò Ï∞ΩÏûë ÎåÄÎ≥∏ÏùÑ Îã§Î•∏ Î∞∞Ïö∞Îì§Í≥º Í≥µÏú†ÌïòÍ≥†, 
              ÌîºÎìúÎ∞±ÏùÑ Î∞õÏïÑÎ≥¥ÏÑ∏Ïöî
            </p>
            <Link to="/add-script" className="btn btn-primary">
              <Heart className="w-5 h-5 mr-2" />
              ÎåÄÎ≥∏ Îì±Î°ùÌïòÍ∏∞
            </Link>
          </section>

        </div>
      </div>
    </div>
  );
};

// ScriptListItem Ïª¥Ìè¨ÎÑåÌä∏
const ScriptListItem = ({ script }) => {
  return (
    <Link
      to={`/scripts/${script._id}`}
      className="script-item group"
    >
      <div className="flex items-start justify-between">
        <div className="flex-1">
          <h3 className="font-semibold text-gray-900 group-hover:text-emerald-600 mb-2">
            {script.title}
          </h3>
          <p className="text-sm text-secondary mb-3 line-clamp-2">
            {script.situation}
          </p>
          <div className="flex items-center gap-4 text-xs text-gray-500">
            <span className="flex items-center">
              <Eye className="w-3 h-3 mr-1" />
              {script.views || 0}
            </span>
            <span className="flex items-center">
              <Calendar className="w-3 h-3 mr-1" />
              {new Date(script.createdAt).toLocaleDateString()}
            </span>
          </div>
        </div>
        {script.emotion && (
          <span className="emotion-tag ml-4">
            {script.emotion.name}
          </span>
        )}
      </div>
    </Link>
  );
};

export default Home;
