import React, { useState, useEffect, useCallback } from 'react';
import { Link, useSearchParams } from 'react-router-dom';
import { Search, Users, Eye, Calendar, ChevronLeft, ChevronRight } from 'lucide-react';
import { scriptAPI, emotionAPI } from '../services/api';

const Scripts = () => {
  const [scripts, setScripts] = useState([]);
  const [emotions, setEmotions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [pagination, setPagination] = useState({});
  const [searchParams, setSearchParams] = useSearchParams();

  // URL 파라미터에서 필터 값 가져오기
  const currentPage = parseInt(searchParams.get('page')) || 1;
  const searchTerm = searchParams.get('search') || '';
  const selectedEmotion = searchParams.get('emotion') || '';
  const selectedCharacters = searchParams.get('characters') || '';
  const sortBy = searchParams.get('sort') || 'newest';

  // 로컬 상태
  const [localSearch, setLocalSearch] = useState(searchTerm);
  const [localEmotion, setLocalEmotion] = useState(selectedEmotion);
  const [localCharacters, setLocalCharacters] = useState(selectedCharacters);
  const [localSort, setLocalSort] = useState(sortBy);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      const params = {
        page: currentPage,
        search: searchTerm,
        emotion: selectedEmotion,
        characters: selectedCharacters,
        sort: sortBy
      };

      // 빈 값 제거
      Object.keys(params).forEach(key => {
        if (!params[key]) delete params[key];
      });

      const response = await scriptAPI.getAll(params);
      setScripts(response.data.scripts);
      setPagination(response.data.pagination);
    } catch (error) {
      console.error('대본 목록 조회 실패:', error);
    } finally {
      setLoading(false);
    }
  }, [currentPage, searchTerm, selectedEmotion, selectedCharacters, sortBy]);

  const fetchEmotions = useCallback(async () => {
    try {
      const response = await emotionAPI.getAll();
      setEmotions(response.data);
    } catch (error) {
      console.error('감정 목록 조회 실패:', error);
    }
  }, []);

  useEffect(() => {
    fetchData();
    fetchEmotions();
  }, [fetchData, fetchEmotions]);

  const updateSearchParams = (newParams) => {
    const params = new URLSearchParams(searchParams);
    
    Object.entries(newParams).forEach(([key, value]) => {
      if (value) {
        params.set(key, value);
      } else {
        params.delete(key);
      }
    });
    
    // 페이지는 1로 리셋 (검색/필터 변경 시)
    if (newParams.search !== undefined || newParams.emotion !== undefined || 
        newParams.characters !== undefined || newParams.sort !== undefined) {
      params.set('page', '1');
    }
    
    setSearchParams(params);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    updateSearchParams({
      search: localSearch,
      emotion: localEmotion,
      characters: localCharacters,
      sort: localSort
    });
  };

  const handlePageChange = (page) => {
    updateSearchParams({ page: page.toString() });
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">로딩 중...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-8">대본 목록</h1>

        {/* 필터 섹션 */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-8">
          <form onSubmit={handleSearch} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
              {/* 검색 */}
              <div className="lg:col-span-2">
                <input
                  type="text"
                  value={localSearch}
                  onChange={(e) => setLocalSearch(e.target.value)}
                  placeholder="제목, 상황, 감정으로 검색..."
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                />
              </div>

              {/* 감정 필터 */}
              <div>
                <select
                  value={localEmotion}
                  onChange={(e) => setLocalEmotion(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                >
                  <option value="">모든 감정</option>
                  {emotions.map((emotion) => (
                    <option key={emotion._id} value={emotion.name}>
                      {emotion.name}
                    </option>
                  ))}
                </select>
              </div>

              {/* 인물 수 필터 */}
              <div>
                <select
                  value={localCharacters}
                  onChange={(e) => setLocalCharacters(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                >
                  <option value="">모든 인물 수</option>
                  {[1, 2, 3, 4, 5].map((count) => (
                    <option key={count} value={count}>
                      {count}명
                    </option>
                  ))}
                </select>
              </div>

              {/* 정렬 */}
              <div>
                <select
                  value={localSort}
                  onChange={(e) => setLocalSort(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                >
                  <option value="newest">최신순</option>
                  <option value="popular">인기순</option>
                  <option value="oldest">등록순</option>
                </select>
              </div>
            </div>

            <div className="flex justify-between items-center">
              <button
                type="submit"
                className="btn-primary flex items-center"
              >
                <Search className="w-4 h-4 mr-2" />
                검색
              </button>

              <span className="text-sm text-gray-600">
                총 {pagination.totalScripts || 0}개의 대본
              </span>
            </div>
          </form>
        </div>

        {/* 대본 목록 */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
          {scripts.map((script) => (
            <ScriptCard key={script._id} script={script} />
          ))}
        </div>

        {/* 대본이 없는 경우 */}
        {scripts.length === 0 && (
          <div className="text-center py-12">
            <p className="text-gray-600 text-lg">검색 결과가 없습니다.</p>
            <Link to="/" className="text-primary-600 hover:text-primary-700 mt-4 inline-block">
              홈으로 돌아가기
            </Link>
          </div>
        )}

        {/* 페이지네이션 */}
        {pagination.totalPages > 1 && (
          <div className="flex justify-center items-center space-x-2">
            <button
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={!pagination.hasPrevPage}
              className="btn-secondary flex items-center disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <ChevronLeft className="w-4 h-4 mr-1" />
              이전
            </button>

            <div className="flex space-x-1">
              {Array.from({ length: Math.min(5, pagination.totalPages) }, (_, i) => {
                const page = Math.max(1, currentPage - 2) + i;
                if (page <= pagination.totalPages) {
                  return (
                    <button
                      key={page}
                      onClick={() => handlePageChange(page)}
                      className={`px-3 py-2 rounded-lg ${
                        page === currentPage
                          ? 'bg-primary-500 text-white'
                          : 'bg-white text-gray-700 hover:bg-gray-50'
                      }`}
                    >
                      {page}
                    </button>
                  );
                }
                return null;
              })}
            </div>

            <button
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={!pagination.hasNextPage}
              className="btn-secondary flex items-center disabled:opacity-50 disabled:cursor-not-allowed"
            >
              다음
              <ChevronRight className="w-4 h-4 ml-1" />
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

// 대본 카드 컴포넌트
const ScriptCard = ({ script }) => {
  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 transition-shadow hover:shadow-md h-full flex flex-col">
      <h3 className="text-xl font-semibold mb-3 text-gray-800">
        {script.title}
      </h3>
      
      <div className="flex items-center text-sm text-gray-600 mb-3 flex-wrap gap-2">
        <div className="flex items-center">
          <Users className="w-4 h-4 mr-1" />
          <span>{script.characterCount}명</span>
        </div>
        
        <div className="flex items-center">
          <Eye className="w-4 h-4 mr-1" />
          <span>{script.views}</span>
        </div>
        
        <div className="flex items-center">
          <Calendar className="w-4 h-4 mr-1" />
          <span>{new Date(script.createdAt).toLocaleDateString('ko-KR')}</span>
        </div>
      </div>
      
      <div className="mb-4 flex-grow">
        <div className="flex flex-wrap gap-2">
          {script.emotions?.map((emotion, index) => (
            <span key={index} className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-emerald-100 text-emerald-800">
              #{emotion}
            </span>
          ))}
        </div>
      </div>
      
      <Link
        to={`/scripts/${script._id}`}
        className="bg-emerald-600 text-white hover:bg-emerald-700 px-6 py-3 rounded-lg font-medium text-sm transition-all duration-200 border border-transparent focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-emerald-500 inline-block text-center w-full mt-auto"
      >
        자세히 보기
      </Link>
    </div>
  );
};

export default Scripts;
